css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alt.bitworld.to") {
body {
 background-color: #2E4B5E !important;
}
.navWrap {
	background-color: #001F52 !important;
}
.contentbg {
	background-color: #6B6C6E !important;
}
.footer {
	background-color: #14395F !important;
}
.mainpage {
 background-color: #3f4f5a !important;
}
.tableoutborder {
 background-color: #001018 !important;
}
.tableinborder {
 background-color: #000000 !important;
}
.tableinborder1 {
 background-color: #191812 !important;
}
.inposttable {
 background-color:#000000 !important;
}
.inposttable1 {
 background-color:#000000 !important;
}
.browseittable {
 background-color:#000 !important;
}
.overLIB {
 background-color:#191822 !important;
}
.overLIBos {
 background-color:#000E3F !important;
}
.maintable {
 background-color: #313452 !important;
}
.tabletitle {
 background-color: #000019 !important;
}
.tablecat {
 background-color: #000 !important;
}
.tablecat1 {
 background-color: #3E2454 !important;
}
.tablea {
 color: #fff !important;
 background-color: #3B3E48 !important;
}
.tablea a:link, .tablea a:visited, .tablea a:active {
 color: #ffffff !important;
}
.tableb {
 color: #fff !important;
 background-color: #111422 !important;
}
.tableb a:hover {
 color: #fff !important;
}
input {
 color: #fff !important;
 background-color: #000 !important;
}
select {
 color: #fff !important;
 background-color: #000 !important;
}
textarea {
 color: #fff !important;
 background-color: #000 !important;
}
input#searchinput, input#pwfeld , input#nickfeld {
    background: #000 !important;
    color:white !important
}
#dropmenudiv a{
 background-color: #1a4255 !important;
}
#dropmenudiv a:hover{ /*hover background color*/
 background-color: #001A13 !important;
 color: #5ADFC3 !important;
}
.forumline	{
 background-color: #193832 !important;
}

.row1 {
 background-color: #3B3E48 !important;
 color:white !important;
}
.row1 a:link, .row1 a:visited, .row1 a:active {
 color: #AdC7F6 !important;
}
table.categories,table.stdmsg,table.statbox,table.table {
 background-color: #000000 !important;
}
table.categories tr th, table.stdmsg tr th, table.statbox tr th, table.table tr th {
 background-color: #201F17 !important;
}
table.categories tr td, table.stdmsg tr td, table.statbox tr td, table.table tr td {
 background-color: #000 !important;
}
table.sidebox {
 background-color: #000 !important;
}
table.sidebox tr th {
 background-color: #002F47 !important;
}
table.sidebox tr td {
 background-color: #000 !important;
}
table.sidebox ul li a{
color: #874343 !important;
}
table.sidebox ul li a:visited{
color: #A74343 !important;
}
table.sidebox ul li a:hover{ /*hover state CSS*/
color: #A77373 !important;
background-color: #000000 !important;
}
.dropMenu {
background:black !important;
    border:0px !important;
    border-top: 1px solid #402020 !important;
}
.dropMenu .subMenu {
    background:transparent !important;
    border:0px !important;
}
.dropMenu .subMenu div.subItem {
    border:0px !important;
    background-image:none !important;
    color:white !important;
}
.dropMenu .subMenu div.subItem a {
    margin-left:3px !important;
}
.dropMenu .subMenu div.subItemOver {
    border:0px !important;
    background-image:none !important;
    color:white !important;
}
}
