css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("opensuse.org") {
body, html {
    color: rgb(166,166,166) !important;
    background: rgb(22,22,22) !important;
}

h1, h2, h3 {
    color: rgb(166,166,166) !important;
}

.box-header {
    color: rgb(166,166,166) !important;
    background: rgb(36,36,36) !important;
    border-color: rgb(90,90,90) !important;
}

.navigation,
#some-content,
.box,
#description_text,
div#background,
div#search,
.postrow, .userinfo, .userinfo_noavatar, .posthead, .time, .floatcontainer,
.thread_controls, .restore, .bbcode_quote
{
    color: rgb(166,166,166) !important;
    background: rgb(36,36,36) !important;
    box-shadow: none !important;
    border-color: rgb(90,90,90) !important;
}

.toc,
.intro-wrapper,
.warning-container,
.mw-content-ltr div {
    color: rgb(166,166,166) !important;
    background: rgb(22,22,22) !important;
    border: none !important;
}

.toc {
    box-shadow: 1px 1px 1px #222 inset, -1px -1px 1px #555 inset !important;
    border-radius: 5px !important;
}

.toc ul {
    padding-left: 1em !important;
}

.navigation a {
    color: rgb(166,166,166) !important;
}

.box-footer {
    background: none !important;
    border-color: rgb(90,90,90) !important;
}

.CodeMirror {
    background: rgb(22,22,22) !important;
}

.CodeMirror-linenumber {
    background: rgb(33,33,33) !important;
}

input, textarea {
    color: rgb(200,200,200) !important;
    background: rgb(22,22,22) !important;
}

li {
    border-color: rgb(90,90,90) !important;
}

li.selected {
    background: rgb(60,60,60) !important;
}

td {
    border: 1px solid rgb(60,60,60) !important;
}

td.sorting_1 {
    background: rgb(10,10,10) !important;
}

tr:hover {
    background: rgb(70,70,70) !important;
}

td.allcow, td.allrow {
    background: rgb(50,50,50) !important;
}

tr.odd {
    background: rgb(25,25,25) !important;
}

tr.even {
    background: rgb(30,30,30) !important;
}

th {
    color: rgb(166,166,166) !important;
    background: rgb(44,44,44) !important;
}

a {
    color: rgb(100,166,140) !important;
    text-shadow: none !important;
}

a:visited {
    color: rgb(166,100,140) !important;
}

a.new {
    color: rgb(200,166,166) !important;
    text-shadow: none !important;
}

pre {
    background-color: rgb(22,22,22) !important;
    color: rgb(200,200,200) !important;
    box-shadow: 1px 1px 1px rgb(20, 40, 0) inset,
        -1px -1px 1px rgb(40,80,0) inset !important;
    border: none !important;
    border-radius: 3px !important;
}

/* build.opensuse.org */
input#search {
    height: 18px !important;
}

/* software.opensuse.org */
.search-result-box {
    background: rgb(20,20,20) !important;
    box-shadow: 1px 1px 1px rgb(50,50,50) inset,
        -1px -1px 1px rgb(40,40,40) inset !important;
}

}
