css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hitbox.tv") {

/*******************************BROWSE MENU ******************************/
    
.subNav.ng-scope
{
background-color: black !important;
max-width: 1400px !important;
}

.subNav.ng-scope [ui-animate=""]
{
   width: 20% !important;
}

h7.active
{
color: #ececec !important;
border-color: darkred !important;
}
h7
{
color: #ececec !important;
}

H4.ng-binding, .subNav .title
{
border-bottom: 1px solid #ececec !important;
color: #ececec !important;
}
.tViewers.icon-eye.ng-binding, .tViewers.ng-binding
{
        margin-left: -20px !Important;
position: relative !important;
color: lightgreen !important;
}

img.ng-scope
{
border: 1px solid #1f1f1f!important;
}

.tMetaSmall.ng-binding
{
color: lightgreen !important;
}

    DIV.pageContent, .view.ng-scope
    {
        background: black !important;
    }
    
        .gtTitle.ng-binding, .ng-binding
    {
        background: transparent !important;
    }
    
    [ng-repeat="game in games"]
    {
      width: 14% !important;
    }
/*************************************************************************/


/*******************************Stream Page Background ******************************/
.meta, .userContent.cf, .ng-pristine.ng-valid
{
background: none !important;
background-color: #000 !important;
}
.status.ng-binding, .game.ng-binding, .hover, label, .detail .meta .name, .tStatus.ng-binding,
.ng-binding > p
{
color: #ececec !important;
}

.tTitle
{
color: darkred !important;
}

.hover.active
{
color: #ececec !important;
border-bottom: 6px solid darkred !important;
}

.link, .embed
{
border: none !important;
}

.followBubble.ng-binding
{
color: #ececec !important;
}

.detailInner, .userContent, .meta
{
background-color: #000 !important;
border-bottom: 1px solid grey !important;
}

.viewers.fltR.icon-eye.ng-binding
{
color: #ececec !important;
background: black !important;
}

BUTTON.icon-ok.following.btnBlueHover.ng-binding, .followBubble.ng-binding
{
color: #ececec !important;
}

/***********************END Stream Page Background ******************************/


/*********************************** CHAT ********************************/
[onmouseover="this.style.borderColor='#2E2E2E'"], [onmouseover="this.style.borderColor='#585858'"]
{
color:#00FF00 !important;
} 

[onmouseover="this.style.borderColor='#848484'"], [onmouseover="this.style.borderColor='#0404B4'"]
{
color:#ffd39b !important;
}

[onmouseover="this.style.borderColor='#5F04B4'"], [onmouseover="this.style.borderColor='#5858FA'"]
{
color:#00eeee !important;
}

[onmouseover="this.style.borderColor='#0B610B'"], [onmouseover="this.style.borderColor='#045FB4'"]
{
color:#87CEEB !important;
}

[onmouseover="this.style.borderColor='#0404B4'"], [onmouseover="this.style.borderColor='#0000FF'"]
{
color:#eec900 !important;
}

[onmouseover="this.style.borderColor='#848484'"]
{
color:#eec900 !important;
}
    
.icon-cog.hoverG2
{
color: #ececec !important;
}

.chatInput.ng-scope
{
border-top: 1px solid grey !important;
background-color: black !important;
}

.chatBody.ng-scope.fontSize1, .chatBody li.admin
{
background-color: #000 !important;
}

SPAN.message.ng-binding
{
color: #ececec !important;
}

.bufferTimestamp, .ng-binding
{
background-color: #000 !important;
border: none !important;
}

.textareaWrap
{
background-color: #000 !important;
border: none !important;
}
[placeholder]
{
color: #ececec !important;
}
    
[ng-repeat="message in messages"]
{
background-color: #000 !important;
border: none !important;
}
    
    [class="message ng-binding"]
    {
        font: 13px/1.5 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
    }
    
    .chatBody .name 
    {
        font: 13px/1.5 "Helvetica Neue",Helvetica,Arial,sans-serif !important;
        font-weight: bold !important;
    }

    .chatBody .title, .chatBody .message
    {
        color: #ececec !important;
    }
    
/*************************************************************************/
    
/*****************POLL**************************************************/
        
        .poll
        {
           background-color: #000 !important;
           border: none !important;
        }
        .question.chatWidth > .ng-binding
        {
           color: #ececec !important;
        }
/**************END POLL**************************************************/

/******************** Search Menu ***********************************************/
.head
{
color: #ececec !important;
}
/********************************************************************************/

/******************** Home Menu ***********************************************/
.homeContent
{
background-color: black !important;
}

.homeContent section, .homeFeaturedCarousel
{
background-color: black !important;
background-image: none !important;
}

/****************************************************************************/

/******************** Game Menu ***********************************************/
.pageContent.gW, .scrollDiv.bg#ececec.ng-scope.ps-container
{
background-color: black !important;
}

H1.ng-binding
{
color: #ececec !important;
}
/******************************************************************************/

/*******************Settings Menu*************************************************/
UL.chatBody.ng-scope.fontSize0, UL.chatBody.ng-scope.fontSize1, UL.chatBody.ng-scope.fontSize2, UL.chatBody.ng-scope.fontSize3, UL.chatBody.ng-scope.fontSize4
{
background-color: black !important;
}

.chatUsers.ng-scope.ps-container.fontSize1, .chatUsers.ng-scope.ps-container.fontSize0, .chatUsers.ng-scope.ps-container.fontSize2, .chatUsers.ng-scope.ps-container.fontSize3, .chatUsers.ng-scope.ps-container.fontSize4
{
background-color: black !important;
}

.size0.hoverG2bg, .size1.hoverG2bg, .size2.hoverG2bg, .size3.hoverG2bg,.size4.hoverG2bg
{
background-color:#3f3f3f !important;
}

A.ng-binding
{
color: lightgreen !important;
}

h5
{
color: #ececec !important;
}

DIV.chatSettings.nono.ui-show, .chatSettings.nono
{
background-color: black !important;
}

.fontSize, .icon-popup.hoverG2, .icon-cancel.hoverG2
{
color: #ececec !important;
}

/********************************************************************/
    
/**********************************Livestream Settings*************************/

.userSubNav.settingsNav.ng-scope, .settingsNav.settingsNavWrap.nono, .innerPad.ng-scope
{
background-color: black !important;
}
    [ng-show="create"] > h1, [ng-show="create"] > h2, [ng-show="create"] > h3, .form_enclosed.formL.ng-invalid.ng-invalid-required.ng-dirty > h2,
    [class="form_enclosed formL ng-dirty ng-valid ng-valid-required"] > h2
    {
        color: #ececec
    }
    
    [ng-class="{error: error.media_status}"], .form_enclosed.formL.ng-invalid.ng-invalid-required.ng-dirty > section, .select2-choice, .select2-results, .select2-search
    ,.scrollDiv.ng-scope.ps-container, .select2-no-results, DIV#select2-drop.select2-drop.select2-display-none.select2-with-searchbox.select2-drop-active
    
    {
        background-color: black !important;
    }
    
    .select2-input
    {
        background-color: #4a4b4b !important;
    }
    
    .select2-input.select2-focused
    {
        background-color: #333535 !important;
    }
    
    .form_enclosed.formL.ng-invalid.ng-invalid-required.ng-dirty > section, [class="form_enclosed formL ng-dirty ng-valid ng-valid-required"]>section
    {
        background-color: black !important;
        border: 1px solid #ececec !important;
    }
    
    .select2-search-choice-close, .select2-drop-mask, .select2-drop, .fb-root
    {
        color: #ececec !important;
    }

    .select2-choice, .select2-choice > span, .select2-input, .select2-result-label:hover
    {
        color: lightgreen !important;
    }
/**********************************************************************************/
    
    /********************************Dashboard********************************/
    .btnGreen.adBtn > .ng-binding
    {
        color: white !important;
        background: none !important;
    }
    
    .ng-dirty.ng-valid.ng-valid-required, .item.ng-scope, .votes.ng-binding > span, .item.ng-scope, .votes.ng-binding
    {     
        color: white !important;   
    }
 
    .item.ng-scope, [ng-repeat="choice in poll.choices"], .ng-dirty.ng-valid.ng-valid-required
    {
        background-color: black !important;  
    }
    .votes.ng-binding
    {
       
        margin-right: -10px !important;
        padding-left: 120px !important;
        padding-right: 20px !important;
    }
    
    .status > .ng-binding
    {
        background: none !important;
    }
/**********************************************************************************/
    
    /********************************Details********************************/
    
    .form_enclosed.formL.ng-valid.ng-dirty > section, .settingsContent.livestreams, .settingsContent.videos, .settingsContent.recordings, .pageHeader, .settingsContent.account,
    .settingsContent.following, .settingsContent
    {
        background: black !important;
    }
    
    .streamSettings, .streamDetails.cf, .form_enclosed.formL.ng-valid.ng-dirty > section
    {
        background-color: black !important;
        border: 1px solid #ececec !important;
    }
    
    .key.fltR.showKey input, .url.fltL input, .form_enclosed.formL.ng-valid.ng-dirty > h2, .sectionTitle.cf > h2, H3.fltR
    {
        color: white !important;
    }
    /********************************End Details********************************/

    /********************************INBOX********************************/
    .settingsContent h1
    {
        color: white !important;
    }  
    /********************************INBOX********************************/
  
/******************************** Account Details ********************************/
    .innerPad > .form_enclosed.formL.ng-pristine.ng-invalid.ng-invalid-required > section
    {
        background: black !important;
        border: 1px solid #ececec !important;
    }
    
    .email.text.ng-pristine.ng-invalid.ng-invalid-required, .password.text.ng-pristine.ng-invalid.ng-invalid-required, .password.text.ng-pristine.ng-invalid.ng-invalid-required
    {
        background-color: black !important;
    }
    
    .icon-upload.btn.btnOutline.uploadBtn.ng-binding
    {
        color: #ececec  !important;
        border: 1px solid #ececec !important;
    }
/**********************************************************************************/
    
    /******************************** following ********************************/
    [ng-repeat="user in following"], .btn.btnGreen.ng-binding
    {
        background-color: black !important;
        border: 1px solid darkred !important;
    }
    
    [class="ng-binding"], .btnOutline.btnDisabled
    {
        color: #ececec  !important;
    }
    .col1, .col0, .listHeader, .ng-valid.ng-dirty, .listContent.followList, .scrollDiv.ng-scope.ps-container
    {
        background-color: black !important;
        border: none !important;
        border-radius: 0px !important;
        box-shadow: none !important;
    }
/**********************************************************************************/

    
/*******************************Channel Description*******************************/
    [ng-show="!media.media_description"], .ng-valid.ng-dirty, .ng-pristine.ng-valid, .info
    {
        color: #ececec  !important;
    }
    [href="http://help.hitbox.tv/customer/portal/articles/1431375-your-profile-page"]
    {
        color: lightblue  !important;
    }
/**********************************************************************************/
}
