css:
@-moz-document domain("etf2l.org") {

#sidebarRight a, a, a:visited,a:hover
{
  color:#b35215 !important;
}

#sidebarHeader a, .notification a
{
  color:#FF6C00!important;
}

body
{
  background:#222!important;
}

#wpbody,.form-table .pre,.ui-autocomplete li a
{
  background:#eee!important;
}

#Nav
{
  width:1100px!important;
  background:rgba(0,0,0,0.6)!important;
  position:relative!important;
  top:-30px!important;
  border:0!important;
  font-family:'Open Sans' !important;
}

#content
{
  width:645px!important;
  margin-top:-32px!important;
  padding:10px!important;
}

#content + div
{
  position:absolute!important;
  top:100px!important;
  right:-311px!important;
  padding:0!important;
}

#header .logo a
{
  width:290px!important;
  height:60px!important;
  margin:0!important;
}

h1
{
  border:0!important;
  font-family:Montserrat!important;
  font-size:18pt!important;
  -webkit-text-stroke:.5px!important;
}

.widget_notifications
{
  position:absolute!important;
  text-align:center!important;
  top:5px!important;
  width:180px!important;
  left:420px!important;
  color:#FFF!important;
}

#search-3
{
  position:absolute!important;
  top:5px!important;
  left:625px!important;
}

#login-3
{
  position:absolute!important;
  right:5px!important;
  width:290px!important;
}

h2
{
  text-transform:none!important;
  border:0!important;
  font-family:Montserrat!important;
  font-size:13pt!important;
  -webkit-text-stroke:.5px!important;
  padding:3px 0!important;
}

ul li ul.children
{
  top:30px!important;
}

ul.children li a
{
  background-color:rgba(0,0,0,0.8)!important;
}

ul#Nav li a
{
  line-height:30px!important;
  background-image:none!important;
  color: #fff!important;
}

ul#Nav li a:hover
{
  background-image:none!important;
}

#header
{
  top:0!important;
  background-position:0 56%!important;
  height:92px!important;
  width:1100px!important;
}

#sidebarHeader
{
  border:0!important;
  height:87px!important;
  width:795px!important;
  background:#3E5C76!important;
  margin:0!important;
  padding:5px 10px 0 0!important;
}

#sidebarHeader .widget,.widget_notifications
{
  background:rgba(0,0,0,0.1)!important;
  height:43px!important;
  padding:5px 10px!important;
}

#sidebarHeader ul
{
  margin:0!important;
}

#sidebarHeader ul li ul li
{
  display:inline!important;
  padding-left:15px!important;
  font-size:14px!important;
}

#sidebarHeader h2
{
  border:0!important;
  font-size:10pt!important;
  -webkit-text-stroke:0!important;
  font-weight:normal!important;
  text-align:center!important;
  padding:0!important;
}

.widget_table tfoot td
{
  font-size:10pt!important;
}

#content > .post
{
  background:#f5f5f5!important;
  border:1px solid #CCCCCC!important;
  overflow:hidden!important;
}

#content .post {
  padding:10px!important;
}

.rec, .commentlist li {
  background:#f9f9f9 !important;
  border:1px solid #CCCCCC!important;
  padding: 10px !important;
}

.post > .postmeta
{
  border:0!important;
  background:none!important;
  font-size:10pt!important;
  padding:0!important;
}

.post > .postmeta a:last-child,.more-link,.navigation
{
  display:block!important;
  font-family:Montserrat!important;
  font-size:13pt!important;
  font-weight:normal!important;
  -webkit-text-stroke:.5px!important;
  margin-top:16px!important;
  border:0!important;
}

blockquote
{
  background:#eee!important;
  border:0!important;
  font-size:9pt!important;
  padding:10px!important;
}

#rs-discuss .post
{
  float:none!important;
  width:605px !important;
  padding:0 10px!important;
}

#rs-discuss .details
{
  position:relative!important;
  left:-1px!important;
  top:-1px!important;
  float:none!important;
  display:block!important;
  width:100%!important;
  height:28px!important;
  background:#3E5C76!important;
  color:#fff!important;
  overflow:hidden!important;
  padding:3px 1px!important;
}

#rs-discuss .details a
{
  color:#fff!important;
}

#rs-discuss .details p a
{
  border:0!important;
  margin-right:20px!important;
}

#rs-discuss .details > p:first-of-type
{
  position:absolute!important;
  left:32px!important;
  display:inline-block!important;
  font-family:Montserrat!important;
  font-size:14pt!important;
  -webkit-text-stroke:.3px!important;
  margin-left:5px!important;
}

#rs-discuss .details > p:nth-of-type(2)
{
  position:absolute!important;
  display:block!important;
  left:3px!important;
}

#rs-discuss .details > p:nth-last-of-type(2)
{
  position:absolute!important;
  top:5px!important;
  right:5px!important;
  display:block!important;
  font-size:12pt!important;
  margin:0!important;
}

#rs-discuss .details img
{
  width:28px!important;
  height:auto!important;
}

#rs-discuss .post-block,#rs-discuss .post-block-odd
{
  font-size:10pt!important;
  border:1px solid #CCCCCC!important;
  margin-bottom:10px!important;
  background:#f8f8f8 !important;
  padding:0 0 10px!important;
}

#rs-discuss .post-wrapper
{
  border:0 !important;
  background:none !important;
}

#rs-discuss .table-list
{
  background:#f5f5f5!important;
}

#rs-discuss .table-list td
{
  border:1px solid #CCC!important;
}

#rs-discuss th
{
  background:#eee!important;
  border:1px solid #CCC!important;
  color:#000!important;
}

#rs-discuss blockquote
{
  background:#F2f2f2!important;
  border:1px solid #CCC!important;
  font-size:10pt!important;
  font-family:inherit!important;
}

#rs-discuss h2
{
  font-size:18pt!important;
}

#rs-discuss .breadcrumbs
{
  background:none !important;
  border:0 !important;
  font-size:13pt !important;
}

#rs-discuss .page-list
{
  font-size:12pt!important;
}

#rs_discuss_search
{
  border:1px solid #CCCCCC!important;
  width:160px!important;
}

#rs-discuss .cell-name,#rs-discuss .cell-ractivity
{
  font-size:11pt!important;
  padding-left:5px!important;
}

.fix a
{
  color:#002bb8!important;
}

.fix td
{
  border:1px solid #AAAAAA!important;
}

.fix tr.fix_title td,.fix td.fix_title,.fix tr.fix_title th
{
  background:none!important;
  color:#000!important;
}

div.post-block
{
  background:#F5F5F5!important;
}

.sidebar
{
  background:#f5f5f5!important;
  border:1px solid #CCCCCC!important;
  margin-right:10px!important;
  margin-top:-22px!important;
}

.bronze
{
  background:#BB8644!important;
}

.dropped
{
  color:#666!important;
}

tr.fix_title td,tr.fix_title th, .cup th
{
  background:#f2f2f2!important;
  color:#000000!important;
}

.gold
{
  background:#ffdf61!important;
}

.green
{
  background:rgb(208,240,192)!important;
}

.red
{
  background:#fcc!important;
}

.silver
{
  background:#cbcbcb!important;
}

#wrapper
{
  background:#f0f0f0!important;
  font-family:'Open Sans'!important;
  border:0!important;
  font-size:10pt!important;
  width:1100px!important;
}

.commentlist li,#commentform input,#commentform textarea
{
  font-size:11pt!important;
}

.commentlist,.commentlist li
{
  margin:20px 0!important;
}

.commentlist {
  padding: 0 !important;
}

.commentlist .postmeta
{
  border:0!important;
  background:none!important;
  font-size:9pt!important;
  margin:5px 0!important;
  padding:0!important;
}

#footer
{
  background:#f2f2f2!important;
  border-top:1px solid #CCC!important;
  color:#000!important;
  height:auto!important;
}

.cup
{
  font-size:13px!important;
}

#sidebarRight
{
  width:210px!important;
}

#sidebarSponsor
{
  width:156px!important;
}

#eventcontent
{
  background:#f5f5f5!important;
  font-size:9pt!important;
  border:1px solid #CCCCCC!important;
  max-height:320px!important;
  padding:10px!important;
  clear:right!important;
}

#eventtabs
{
  display:none!important;
  visibility:hidden!important;
}

.etf2l_post object
{
  display:block!important;
  margin:auto!important;
}

.map,.result
{
  font-size:16pt!important;
}

.result
{
  font-family:Montserrat!important;
  -webkit-text-stroke:.5px;
}

.mat p
{
  font-family:Montserrat!important;
  -webkit-text-stroke:.5px;
  text-align:center!important;
  background:#f5f5f5!important;
  border:1px solid #CCCCCC!important;
  margin:0 0 10px!important;
  padding:10px!important;
}

.mat + script + div
{
  display:none!important;
  float:none!important;
  text-align:center!important;
  margin-bottom:3px!important;
}

.widget_notifications .looser
{
  color:#FFF!important;
}

.notification
{
  list-style-image:none!important;
  margin:3px 0 0!important;
}

#wpbody,.fix > p
{
  padding:10px!important;
}

#Nvt,#search-3 p,h4 img,.post > .postmeta img,#rs-discuss .details br,#rs-discuss .details p,#rs-discuss .userinfo,#footer img,#footer p:first-child
{
  display:none!important;
}

#search-3 h2,#sidebarHeader ul li
{
  text-align:center!important;
}

h2 > strong,.etf2l_page h2,.etf2l_post h2,.etf2l_post a,.etf2l_page a,.mat b
{
  font-weight:normal!important;
}

h4,.sidebar ul,.sidebar ol,.widget_table tbody td
{
  font-size:10pt!important;
}

input#search_input,.fix
{
  background:#f5f5f5!important;
  font-size:10pt!important;
}

#rs-discuss .rowgroup,.fix tr td,.fix tr th
{
  background:none!important;
}

#rs-discuss .category,.rec_search,.fix tr.comp td,.thistory
{
  background:#f2f2f2!important;
}

table.teaminfo td {
  border:0!important;
}
}
