css:
@-moz-document regexp("https?:\\/\\/(?!([\\w-]*).mail)(?!mail|imgur|plotagon|mapsengine)[\\w]*\\.(?!redtube|youporn|yahoo).*") {
:root {
  -webkit-filter: invert(.95) hue-rotate(180deg) !important;
  -moz-filter: invert(.95)  hue-rotate(180deg) !important;
  -o-filter: invert(.95)  hue-rotate(180deg) !important;
  -ms-filter: invert(.95)  hue-rotate(180deg) !important;
      
}

iframe,  

  
*[class]:empty,  
*[id]:empty,  
    
*[style*="url"],
*[data-default-background-image],
*[data-background-image],
   
img, 
svg, 
canvas,
  
video,  
audio,


embed,
object {
  -webkit-filter: invert(1) hue-rotate(180deg) !important;
  -moz-filter: invert(1)  hue-rotate(180deg) !important;
  -o-filter: invert(1)  hue-rotate(180deg) !important;
  -ms-filter: invert(1)  hue-rotate(180deg) !important;
      
}

object > object, 
object > embed  {
  -webkit-filter: invert(0)  !important;
  -moz-filter: invert(0)  !important;
  -o-filter: invert(0)  !important;
  -ms-filter: invert(0)  !important;
      
}     
     
:-webkit-full-screen  {
  -webkit-filter: invert(100%) hue-rotate(180deg)  saturate(125%);
}

:-moz-full-screen  {
  -moz-filter: invert(100%)  hue-rotate(180deg) saturate(125%);
}

:-ms-full-screen  {      
  -ms-filter: invert(100%)  hue-rotate(180deg) saturate(125%);
}
                  
@supports not (body: background-image) {
  body {
          background-color: #FFFAFA !important; 
          background-image: none !important; 
  }
                    
}

:root input {
        color : #FFF !important;
       background-color: #444 !important;
}
      
::selection,
::-moz-selection,
::-webkit-selection,
::-o-selection,
::-ms-selection {
	background: #0000ff;
    color: #fff;
}

a:hover {
	-webkit-transform: scale(1.25);
  	-moz-transform: scale(1.25);
	-o-transform: scale(1.25);
	-ms-transform: scale(1.25);
}
 
 ::-webkit-scrollbar{width:3px; height:3px; }
 ::-webkit-scrollbar-track-piece{background-color:white; -webkit-border-radius:3px;}
 ::-webkit-scrollbar-thumb:vertical{background-color:#666; -webkit-border-radius:3px;}
 ::-webkit-scrollbar-thumb:horizontal{background-color:#666; -webkit-border-radius:3px;}  

 ::-moz-scrollbar{width:3px; height:3px;}
 ::-moz-scrollbar-track-piece{background-color:white; -webkit-border-radius:3px;}
 ::-moz-scrollbar-thumb:vertical{background-color:#666; -webkit-border-radius:3px;}
 ::-moz-scrollbar-thumb:horizontal{background-color:#666; -webkit-border-radius:3px;} 


 ::-o-scrollbar{width:3px; height:3px;}
 ::-o-scrollbar-track-piece{background-color:white; -webkit-border-radius:3px;}
 ::-o-scrollbar-thumb:vertical{background-color:#666; -webkit-border-radius:3px;}
 ::-o-scrollbar-thumb:horizontal{background-color:#666; -webkit-border-radius:3px;} 

 ::-ms-scrollbar{width:3px; height:3px;}
 ::-ms-scrollbar-track-piece{background-color:white; -webkit-border-radius:3px;}
 ::-ms-scrollbar-thumb:vertical{background-color:#666; -webkit-border-radius:3px;}
 ::-ms-scrollbar-thumb:horizontal{background-color:#666; -webkit-border-radius:3px;}
          
          
header, footer, nav, article, section, aside {
	contenteditable: true !important;
	draggable: true !important;
	spellcheck: false !important;
}
}

@-moz-document regexp("https?:\\/\\/(?!([\\w-]*).mail)(?!mail|imgur|plotagon|mapsengine)[\\w]*\\.(?!redtube|youporn).*") {
*[id*="ero"],
*[class*="ero"],

*[id*="branding"],
*[class*="branding"]{
  -webkit-filter: invert(1) hue-rotate(180deg) !important;
  -moz-filter: invert(1)  hue-rotate(180deg) !important;
  -o-filter: invert(1)  hue-rotate(180deg) !important;
  -ms-filter: invert(1)  hue-rotate(180deg) !important;
      
}
}
