css:
@-moz-document domain("facebook.com") {
#facebook body:not(.plugin):not(.transparent_widget):not([style*="#f2f2f2"]) {
/* green */ 
background: #fff url(http://www.massappealdesigns.com/wp-content/uploads/2013/10/Breaking-Bad-Wallpaper.jpg) center center repeat fixed !important;} 
#facebook #jewelContainer .hasNew .jewelCount {background-color: #009600 !important;}
#facebook #jewelContainer .hasNew:hover .jewelCount {background-color: #00b000 !important;}

#facebook body:not(.UIPage_LoggedOut):not(.privacyPolicyPage) #globalContainer,
#facebook timelineLayoutLoggedOutUserProfile #globalContainer,
#facebook .timelineLayoutLoggedOutProfile #globalContainer {
background-color: transparent !important;
border: none !important;
}
}
