css:
@font-face {
  font-family: 'Imprima';
  font-style: normal;
  font-weight: 400;
  src: local('Imprima'), local('Imprima-Regular'), url(http://themes.googleusercontent.com/static/fonts/imprima/v1/8ZNQ9eOlWj2xmC8HToxmXw.woff) format('woff');
}

@font-face {
  font-family: 'Francois One';
  font-style: normal;
  font-weight: 400;
  src: local('Francois One Regular'), local('FrancoisOne-Regular'), url(http://themes.googleusercontent.com/static/fonts/francoisone/v6/bYbkq2nU2TSx4SwFbz5sCBa1RVmPjeKy21_GQJaLlJI.woff) format('woff');
}

* {
  font-size:inherit !important;
  font-family: 'Imprima', sans-serif !important;
  font-size-adjust: none !important;
  letter-spacing:.06em;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Francois One', sans-serif !important;
}

h1[class] { font-size: 1.75em !important; margin-bottom: 0.765em !important;}
h2[class] { font-size: 1.5em !important;  margin-bottom: 0.855em !important; }
h3[class] { font-size: 1.25em !important;  margin-bottom: 0.956em !important; }
h4[class] { font-size: 1em !important;  margin-bottom: 1.161em !important; }
h5[class] { font-size: .75em !important;  margin-bottom: 1.238em !important; }
h6[class] { font-size: .5em !important;  margin-bottom: 1.238em !important; }

p  {  
  padding: 0 .3em 0 .3em !important;
  text-indent:1.625em !important;
  text-align:justify !important;
  line-height: 1.5em !important;
}

body > p {   padding: 0 10em 0 10em !important;  }
