_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.big-button.b-w,
.big-button.t-w,
.big-button.w-b,
.not-touch.list-view .meta-tags div,
.small-button,
.small-button.b-w.pull-right,
.submit-blue,

a.submit-blue
{	
	background: radial-gradient(circle, #0f79afa8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.big-button.b-w::before,
.big-button.t-w::before,
.big-button.w-b::before,
.not-touch.list-view .meta-tags div::before,
.small-button.b-w.pull-right::before,
.small-button::before,
.submit-blue::before,

a.submit-blue::before
{
