css:
@-moz-document regexp('http://www.google.*'), regexp('https://www.google.*') {

html {
	min-height: 100%;
}

a {
	-webkit-transition: all .2s ease-in-out;
	-moz-transition: all .2s ease-in-out;
	transition: all .2s ease-in-out;
}
#searchform {
	z-index:9999;
}
#mngb {
	position:relative;
	z-index:999;
}
body#gsr {
	background:#111;
	min-height:100%;
	height:100%;
}
body#gsr:after {
	content:'';
	display:block;
	position:fixed;
	left:0;
	top:0;
	width:100%;
	height:100%;
	background:#111 url("http://i.imgur.com/3NWRttc.jpg") 50% 20% no-repeat !important;
	background-size: cover !important;

	-webkit-transform: rotate(.8deg) scale(1.1);
	-moz-transform: rotate(.8deg) scale(1.1);
	-o-transform: rotate(.8deg) scale(1.1);
	transform: rotate(.8deg) scale(1.1);
}

#gbqfqw {
	background:rgba(255,255,255,.6);
}
#lga {
	position:relative;
	z-index:300;
}
#lga img {
	display:none;
	background:url(http://x.co/utmgif);
}
#lga:after {
	display:block;
	content:'Moin Sion';
	font:normal 128px/200px 'Trebuchet ms',serif;
	color:#fff;
	text-shadow: 0 1px 0 #ccc, 0 2px 0 #c9c9c9, 0 3px 0 #bbb, 0 4px 0 #b9b9b9, 0 5px 0 #aaa, 0 6px 1px rgba(0,0,0,.1), 0 0 5px rgba(0,0,0,.1), 0 1px 3px rgba(0,0,0,.3), 0 3px 5px rgba(0,0,0,.2), 0 5px 10px rgba(0,0,0,.25), 0 10px 10px rgba(0,0,0,.2), 0 20px 20px rgba(0,0,0,.15);
	-webkit-transform: scaleY(.85);
	-moz-transform: scaleY(.85);
	transform: scaleY(.85);
}
#lga div {
	padding:0 !important;
}
#footer {
	position:fixed !important;
}
#footer>div {
	border:0;
	background:rgba(255,255,255,.6);
	height:22px;
}

div.mw {
	background:#fff;
}

div#flci {
	display:none !important;
}

#main #footer a {
	color:#222 !important;
	text-shadow:0 1px 1px rgba(255,255,255,.6) !important;
}
#main #footer a:hover {
	text-decoration:none !important;
	color:#fff !important;
	text-shadow:none !important;
}

#rcnt {
	background:#fff !important;
}

#prm, #hplogo,
#cpBackgroundImg {
	display:none !important;
}

#gbi4t {
	color:#fff !important;
}

#main {
	position:relative;
	z-index:300;
}

body.vsh #gac_scont {
	display:none !important;
	background:none !important;

	-webkit-transition: background .5s ease-in-out;
	-moz-transition: background .5s ease-in-out;
	transition: background .5s ease-in-out;
}
#cnt.big {
	background:#fff !important;
}

#rhs_block .std,
#bottomads,
.pmoabs {
	display:none !important;
}

li.g {
	margin-bottom:0 !important;
	padding-bottom:23px !important;
}
#fll {
	margin:0 auto !important;
	padding:19px auto !important;
}
}
