css:
@-moz-document domain("fourmizzz.fr") {

html{
background:#f1f1f1 !important;
}
#menu{
box-shadow:none !important;
background:#009018 !important;
border-bottom:1px solid #003a1b !important;
}
#menu ul li a{
background:#009018 !important;
}
ul.menu_colonne,#menu ul.menu_colonne li a,#menu ul li:hover a,#menu .boutonActif{
box-shadow:none !important;
background:#00ae1d !important;
}
h2{
color:#007000 !important;
}
.ligneTableau:nth-child(2n+1){
border:1px solid #c8c8c8 !important;
background:white !important;
}
.titre_colonne_cliquable,.boite_connexion_titre{
background:rgba(40,40,40,0.9) !important;
}
.contenu_boite_compte_plus,.contenu_boite_info,.boite_connexion_contenu,.boite_connexion_contenu, .boite_presentation_contenu{
background:rgba(50,50,50,0.9) !important;
top:35px !important;
}
.banniere_accueil{
background:rgba(50,50,50,0.9) !important;
border:12px solid rgba(40,40,40,0.9) !important;
}
.banniere_accueil a{
padding:5px 9px !important;
border:1px solid #009018 !important;
border-radius:8px;
color:white !important;
background: -moz-linear-gradient(bottom, #009018, #59ff74) !important;
}
#centre .titre, #centre h2.css{
color:#666 !important;
}
ligneTableau separateur{
display:none !important;
}
.simulateur, .simulateur_combat,#boite_tdc, #boite_ouvriere{
background:white !important;
border-color:#c8c8c8 !important;
}
.ligne_paire{
background:#f1f1f1 !important;
}
.lien td{
background:none !important;
}
.topic_forum{
color:#222 !important;
font-weight:normal !important;
}
.tableau_ami,.boiteMessagerie,.boite_amelioration,.boite_membre,.tableau_diplomatie_carte, .tableau_fourmiliere_carte, .tableau_detail_carte{
background:white !important;
border:1px solid #c8c8c8 !important;
}
hr,.tableau_score{
border:0 !important;
}
hr{
border-top:1px solid #afafaf !important;
}
#formulairePageEnnemie fieldset,#tabEnnemie tr:nth-of-type(2n+1), .diplomatie tr:nth-of-type(2n+1),.tab_triable tr.alt,#TableMessagerie tr:nth-of-type(4n+4), #tabChoixArmee tr:nth-of-type(2n+1), #TableMessagerie tr:nth-of-type(4n+5){
background-color:#f1f1f1 !important;
}
#TableMessagerie th{
background:#e5e5e5 !important;
}
.propositions tr:nth-child(8n+1),
.propositions tr:nth-child(8n+2),
.propositions tr:nth-child(8n+3),
.propositions tr:nth-child(8n+4){
background:white;
}
.propositions tr{
padding:0px !important;
}
#menu_selection li:hover{
background-color:#e1ffca !important;
}
.contenu_boite_info .lien:hover,.contenu_boite_compte_plus .lien:hover{
background-color:rgba(255,255,255,0.1) !important;
}
.contenu_boite_compte_plus table{
width:100% !important;
}
.categorie_forum,.ligne_paire,#formulairePageEnnemie fieldset{
border:1px solid #a5a5a5 !important;
}
.contenu_boite_compte_plus form{
padding:4px 0;
}
.contenu_boite_compte_plus form input{
vertical-align:middle;
}

}
