css:
@-moz-document domain("ravelry.com") {
body, ol, ul, td, th, input, select, table {
font-family: Gotham,'Lucida Grande', Arial, Helvetica, sans-serif;
}

a:hover,  #notebook_menu li:hover {
text-decoration: none;
background-color: #eee;
}
.notebook_options a:hover, #notebook_menu a:hover {
text-decoration: none;
background-color: transparent;
}

h2.welcome_headline {
font-family: Gotham,'Lucida Grande', Arial, Helvetica, sans-serif;
letter-spacing:-.05em;
}
.box_title {
background: #D9D9DB;
height: 25px;
padding: 5px 10px 0;
padding-bottom: 0px;
color: #3D3D3D;
letter-spacing: 0;
font-size: 1em;
border-radius: 8px 8px 0 0;
border: 1px solid #C7C7C7;
border-bottom: 0;
font-weight: bold;
line-height: 20px;
}
.box_contents {
padding: 5px;
background: #F5F5F5;
border-left: 1px solid #C7C7C7;
border-right: 1px solid #C7C7C7;
}


#page_header {
background: #eee;
margin-bottom: 20px;
}

#navigation > .nav > li {
background: #F7F7F7 !important;
height:38px;
}
#navigation > .nav > li.active, #navigation > .nav > li:hover {
background: #fff !important;

}
#navigation > .nav > li > a {
padding: 10px 2px;
}
#notebook_menu {
top:31px;}

.chiclet #current {
background-color: #FFFFFF;
  padding-left:2px;
}  

table.grid tr.stripe,
.tabs ul li a {
background:#f7f7f7;
}
table#forum_posts thead td, table.grid th, table.grid tr.header td, .tabs ul li span {
background:none;
}

.topics #topic_toolbar {
background:#f7f7f7}
}
