css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://www\\.rockpapershotgun\\.com/(?!forums).*') {
/* The Forums aren't ready yet
@-moz-document domain("www.rockpapershotgun.com") {
 */
    p,li { /* Main Text Color */
        color: #00A000 !important;
        font-family: sans-serif !important;
    }
    
    a { /* Links */
        color: #E85848 !important;
        border: 0 !important; /*upper links*/
    }
    strong, /* Interview Questions */
    p.info a, /* Author */
    div.comment-content cite.fn, /* Comments: Username, no link */
    div.comment-content cite.fn a, /* Comments: Username, link */
    div.comment-content span.comment-meta > a, /* Comments: Date */
    p.log-in-to-comment > a /* you have to be registered, etc */
    {
        color: #508ED8 !important;
        font-size: 15px;
    }
    
    div.entry a, /* Article Links */
    #menu-main-container ul li a, /* Top Links (Features, etc) */
    div.comment-content p a /* Comment Links */
    {
        color: red !important;
    }
    
    h2 a { /* Article Title */
        color: #DBA716 !important;
    }
    
    
    div.content,div.post-inner,div#content,div#page, /* Main Post */
    div#all-comments,div#all-comments * { /* All Comments */
        border: 0 !important;
        border-radius: 0 0 0 0 !important;
        background: black !important;
        border-color: black !important;
    }
    #comments ul.children li.comment-role-administrator { /* Admin Comments */
        background: #151515 !important;
    }
    
    div.block:first-child blockquote, /* Article quotes */
    div#all-comments blockquote /* Comments quotes */
    {
        background: #0c0c0c !important;
    }
    
    input.comment-block { /* Block Text */
        color: #508ED8 !important;
    }
    div.reply { /* Reply boxes */
        background: #242424 !important;
    }
    a.comment-reply-link { /* Reply links */
        color: #508ED8 !important;
    }
    div.block:first-child iframe { /* Embeeded iframes, usually YouTube videos */
        border: 1px solid red !important;
    }
    
    div#all-comments ol.commentlist li { /* Comments */
        border-left: 1px solid red !important;
        background: black !important;
    }
    
    #menu-main-container ul li { /* Top Links' Boxes */
        background: none !important;
        border: 2px solid #508ED8 !important;
        border-image-width: 0 0 0 0 !important;
    }
    
    #menu-main-container ul li:last-child { /* Top Links (Features, etc) */
        background: #508ED8 !important;
    }
    
    #wpadminbar a { /*Top Bar Links */
        color: #CCCCCC !important;
    }
    
    #aec_edit_options, /* Spell checking is for chumps! */
    #commentform fieldset > p:first-child { /* Freedom of speech, etc */
        display: none !important;
    }
    #commentform fieldset textarea { /* Comment Textarea */
        color: #00A000 !important;
        background: #242424 !important;
    }
    #commentform fieldset > input { /* Opinion away! */
        background: #242424 !important;
        color: #508ED8 !important;
    }
    
    /* HOVER */
    a:hover, /* Links */
    div.entry a:hover, /* Article Links */
    div.comment-content p a, /* Comment Links */
    div.comment-content cite.fn a:hover, /* Comments: Username, link */
    div.comment-content span.comment-meta > a:hover /* Comments: Date */
    {
        color: white !important;
    }
    a.comment-reply-link:hover, /* Reply links */
    input.comment-block:hover, /* Block Text */
    #commentform fieldset > input:hover { /* Opinion away! */
        color: red !important;
    }
}
