css:
@-moz-document domain(flashflashrevolution.com) {
/*------------------------------------------------------------------
[Master Stylesheet Re-Code]

Project:		FlashFlashRevolution Revamp
Version:		1.3
Last change:		11/27/13 [cleaned up unnecessary code and organized, justin_ator]
Authors:		Troy Whiteley (tw) (original code) // Justin Faulkner (justin_ator) (modifications)

[Table of contents]

1. Body
2. Html overrides
3. Text Links
6. Alignment
7. News date / .news-date
8. Zebra stripes / .zebra
9. Comment votes
10. Containers
11. Sidebar sections
12. Information boxes
13. Header
14. Footer
15. Comments
16. Profile Menu / #profile_menu
17. Tokens
18. Fixes
19. Forums
-------------------------------------------------------------------*/
 
/*------------------------------------------------------------------
[1. Body]
*/
body {
	background: url('http://i.imgur.com/aHQGyug.png') #79838c !important;
}

/*------------------------------------------------------------------
[2. Html overrides]
*/
h2 {color:#000065 !important;}
h2 a:link,h2 a:hover,h2 a:visited,h2 a:active{text-decoration:none;color:#000065 !important;}
strong{font-weight:bold !important;}

a:link,a:visited,a:active{color:#000065 !important;text-decoration:none;}
a:hover{color:#000065 !important;text-decoration:underline;}


/*------------------------------------------------------------------
[3. Text Links]
*/
.dark-text,.dark-text a:link,.dark-text a:active,.dark-text a:visited{color:#000065 !important;text-decoration:none;}
.dark-text a:hover{color:#000065 !important;text-decoration:underline;}
.light-text,.light-text a:link,.light-text a:active,.light-text a:visited{color:#000065 !important;text-decoration:none;}
.light-text a:hover{color:#000065 !important;text-decoration:underline;}

/*------------------------------------------------------------------
[6. Alignment]
*/
.float-left{float:left;}
.float-right{float:right;}
.right-text{text-align:right;}
.clear{clear:both;}
.left-img-link{float:left;padding:0 6px 0 0;}
.center{text-align:center;}
.spacer{height:20px;}
.small-spacer{height:6px;}
.no-wrap{white-space:nowrap;}

/*------------------------------------------------------------------
[7. News date / .news-date]
*/
.news-date{color:#000065 !important;width:600px;}
.news-date a:link,.news-date a:visited,.news-date a:active{color:#000065 !important;text-decoration:none;}
.news-date a:hover{color:#000065 !important;text-decoration:underline;}

/*------------------------------------------------------------------
[8. Zebra stripes / .zebra]
*/
.zebra1 td{background:#000 !important;}
.zebra2 td{background: #000 !important;}

.css3_zebra tbody tr:nth-child(odd){background:#000065 !important;}
.css3_zebra tbody tr:nth-child(even){background:#000065 !important;}

/*------------------------------------------------------------------
[9. Comment votes]
*/
.comment-vote{padding-right:16px;}
.comment-green{color:#6ABA5F !important;}
.comment-red{color:#BC3D40 !important;}
.hidden-comment{color:#909090 !important;text-align:right;width:100%;}
.hidden-comment a{cursor:pointer;}

/*------------------------------------------------------------------
[10. Containers]
*/
#wrapper{margin:0 auto;width:995px;background:#79838c url('http://i.imgur.com/gNPy2kH.png') repeat-y center !important;}
#site_stats{
	width:999px;
	float:left;
	height:23px;
	background:#000 !important;
	color:#ccc !important;
	padding:3px;
}
#site_stats img{float:right;padding:0 0 0 3px;}
#header{
	width:995px;
	float:left;
	height:107px;
	background: #000 url('http://www.flashflashrevolution.com/images/2008/header-bg-transparent.png') repeat-x !important;
}
#subheader{float:left !important;}
#leftcolumn{
	border-left:1px #656565 solid;
	border-right:1px #999 solid;
	background:#000 !important;
	min-height:350px;
	width:180px;
	float:left;
}
#leftcolumn td,#leftcolumn td img{padding:0;}
#content{
	float:left;
	margin:0;
	padding:10px;
	min-height:350px;
	width:611px;
	display:inline;
  	background:#000 !important;
  	color:#000065 !important;
}
#content p{padding:0 20px 20px 0;}
#content p a:link,#content p a:visited,#content p a:active{color:#000065 !important;text-decoration:none !important;}
#content p a:hover{color:#000065 !important;text-decoration:underline !important;}
#rightcolumn{
	border-left:1px #999 solid;
	border-right:1px #656565 solid;
	color:#333 !important;
	background:#000 !important;
	min-height:350px;
	width:180px;
	float:left;
}

/*------------------------------------------------------------------
[11. Sidebar Sections]
*/
.sidebar-section{color:#000065 !important;padding:10px;border-bottom:1px solid #999 !important;background-color:rgba(20,20,20,0.5) !important;}
#game_list{color: #595959;line-height:120% !important;}
#game_list li{margin:6px 0 !important;}
#game_list a:link,#game_list a:hover,#game_list a:visited,#game_list a:active{color:#595959 !important;text-decoration:none !important;}

#community_list li{margin:6px 0 6px 15px;list-style-image:url('http://www.flashflashrevolution.com/images/2008/bullet.png') !important;list-style-position:outside !important;}
#community_list a:link,#community_list a:visited,#community_list a:active{color:#595959 !important;text-decoration:none !important;}
#community_list a:hover{color:#595959 !important;text-decoration:underline !important;}

#subscriber_list li{margin:6px 0 6px 15px;list-style-image:url('http://www.flashflashrevolution.com/images/2008/bullet.png') !important;list-style-position:outside !important;}
#subscriber_list a:link,#subscriber_list a:visited,#subscriber_list a:active{color:#000065 !important;text-decoration:none !important;}
#subscriber_list a:hover{color:#000065 !important;text-decoration:underline !important;}

#latest_forum_posts li{margin:6px 0 6px 15px;list-style-image:url('http://www.flashflashrevolution.com/images/2008/bullet.png') !important;list-style-position:outside !important;}
#latest_forum_posts a:link,#latest_forum_posts a:visited,#latest_forum_posts a:active{color:#595959 !important;text-decoration:none !important;}
#latest_forum_posts a:hover{color: #595959 !important;text-decoration:underline !important;}

#latest_profiles{color:#000065 !important;line-height:120% !important;}
#latest_profiles li{margin:6px 0;white-space:nowrap;overflow:hidden !important;}
#latest_profiles a:link,#latest_profiles a:hover,#latest_profiles a:visited,#latest_profiles a:active{color:#000065 !important;text-decoration:none !important;}

#latest_videos{color:#595959 !important;line-height:120% !important;}
#latest_videos li{margin:6px 0 6px 0;white-space:nowrap;overflow:hidden !important;}
#latest_videos a:link,#latest_videos a:hover,#latest_videos a:visited,#latest_videos a:active{color:#595959 !important;text-decoration:none !important;}

#random_widget{color:#595959 !important;line-height:120% !important;}
#random_widget li{margin:6px 0;white-space:nowrap;overflow:hidden !important;}
#random_widget a:link,#random_widget a:hover,#random_widget a:visited,#random_widget a:active{color:#595959 !important;text-decoration:none !important;}

#recent_play_container{
	color:#4d5052;
	float:left;
	width:126px;
	overflow:hidden;
	line-height:100%;
}
.recent-play-stat{width:160px;text-align:right;overflow:hidden;}
.recent-play-stat a:link,.recent-play-stat a:hover,.recent-play-stat a:active,.recent-play-stat a:visited{
	width:160px;
	text-align:right;
	text-decoration:none;
	color:#000065 !important;
}

.column-header{color: #000065 !important;}
.column-header a:link,.column-header a:hover,.column-header a:active,.column-header a:visited{color:#000065 !important;text-decoration:none !important;}

/*------------------------------------------------------------------
[12. Information Boxes]
*/
#about_box{width:612px;height:260px;}
#about_close_text{color:#fff !important;padding:10px !important;}
#update_box {
	width:612px;
	background:#e0ecf6 !important;
	color:#595959 !important;
	border:#65a1d4 1px solid !important;
	text-align:left;
}

.input_button {
	background: #cfcfcf url('http://www.flashflashrevolution.com/images/2008/input_button.png') repeat-x !important;
	font-size: 12px;
	border: 1px solid #707070;
	border-radius: 3px;
	padding: 2px 7px 4px;
	height: 14px;
	display: inline-block;
	margin-bottom: 3px;
}

input.input_button, a.input_button, a.input_button:link, a.input_button:visited, a.input_button:active, a.input_button:hover {
	color: #000065 !important;
	text-decoration: none;
	cursor: pointer;
}

input.input_button:hover, a.input_button:hover, input.input_button_active, a.input_button_active  {
	background: #a7d9f5 url('http://www.flashflashrevolution.com/images/2008/input_button_hover.png') repeat-x !important;
	border: 1px solid #3c7fb1;
}

#tour_table{color:#000065 !important;}
.cell{color:#000 !important;}
.cell_username{color:#000 !important;}
.table{background:#000 !important;}
/*------------------------------------------------------------------
[13. Header]
*/
#header_logo{float: left;}
#user_controls {
	background:url('http://www.flashflashrevolution.com/images/2008/user-controls-transparent.png') no-repeat right !important;
	font-family: Tahoma;
	height:108px;
	width:365px;
	overflow:hidden;
	float:right;
	text-align:right;
	vertical-align: middle;
}
#user_control_links{color:#000065 !important;margin:30px 4px 0 0 !important;}
#user_control_links a:link,#user_control_links a:hover,#user_control_links a:active,#user_control_links a:visited{color:#000065 !important;text-decoration:none !important;}

#top_adspace{
	width:748px;
	height:108px;
	border-left:1px #656565 solid;
	border-right:1px #999 solid;
	border-bottom:1px #999 solid;
	background:#dedede !important;
	float:left;
	position:absolute;
}
#top_ad{
	width:728px;
	height:90px;
	margin:10px;
	color:#000065 !important;
}
#top_ad td,#top_ad img{padding:0;}
#subhead_userinfo{
	font-family: Tahoma;
	width:225px;
	height:87px;
	border-right:1px #656565 solid;
	border-bottom:1px #999 solid;
	background:#000 !important;
	float:right;
	padding:10px;
}
#subhead_userinfo .profile_pic{
	max-width:64px;
	max-height:66px;
	padding-right:10px;
	float:left;
}
#userinfo_container{
	color:#4d5052 !important;
	float:left;
	width:150px;
	overflow:hidden;
	line-height:100%;
}
.userinfo-stat{
	width:150px;
	text-align:right;
	text-decoration:none;
	color:#4d5052 !important;
}
.messages a:link,.messages a:hover,.messages a:active,.messages a:visited {
	width:150px;
	text-align:right;
	text-decoration:none;
	color:#4d5052 !important;
}

/*------------------------------------------------------------------
[13. Footer]
*/
#footer_bar{
	margin:0 auto;
	width:993px;
	background:#000 !important;
	border:1px #3d698d solid;
	height:10px;
}
#footer_text{
	margin:0 auto;
	width:993px;
	padding:3px;
	text-align:right;
	color:#000065 !important;
}
#footer_text .light-text{color:#000065 !important;}
#footer_text a:link,#footer_text a:active,#footer_text a:visited{color:#000065 !important;text-decoration:none !important;}
#footer_text a:hover{color:#000065 !important;text-decoration:underline !important;}

/*------------------------------------------------------------------
[15. Comments]
*/
.comment-body {
	width:571px;
	background:url('http://i.imgur.com/RrjS4an.png') top no-repeat !important;
	padding:16px 10px 0 10px;
	margin-top:2px;
	text-align:left;
}
.admin_comment,.admin_comment a:link,.admin_comment a:active,.admin_comment a:visited{color:#CC0000;text-decoration:none;}
.admin_comment a:hover{color:#CC0000;text-decoration:underline;}
.staff_comment,.staff_comment a:link,.staff_comment a:active,.staff_comment a:visited{color:#72ab6d;text-decoration:none;}
.staff_comment a:hover{color:#72ab6d;text-decoration:underline;}
.legend_comment,.subscriber_comment a:link,.subscriber_comment a:active,.subscriber_comment a:visited{color:#778C7A;text-decoration:none;}
.legend_comment a:hover{color:#778C7A;text-decoration:underline;}
.subscriber_comment,.subscriber_comment a:link,.subscriber_comment a:active,.subscriber_comment a:visited{color:#4a84b5;text-decoration:none;}
.subscriber_comment a:hover{color:#4a84b5;text-decoration:underline;}
.simcreator_comment,.simcreator_comment a:link,.simcreator_comment a:active,.simcreator_comment a:visited{color:#e69e20;text-decoration:none;}
.simcreator_comment a:hover{color: #e69e20;text-decoration:underline;}
.commentlist a:link,.commentlist a:active,.commentlist a:visited{color:#595959;text-decoration:none;}
.commentlist a:hover{color:#595959;text-decoration:underline;}

.admin_comment-background{background:#ffc8c8 url('http://www.flashflashrevolution.com/images/2008/comment-header.png') top no-repeat;}
.legend_comment-background{background:#B2D1B6 url('http://www.flashflashrevolution.com/images/2008/comment-header.png') top no-repeat;}
.subscriber_comment-background{background:#b9d2e6 url('http://www.flashflashrevolution.com/images/2008/comment-header.png') top no-repeat;}
.staff_comment-background{background:#a8e3a1 url('http://www.flashflashrevolution.com/images/2008/comment-header.png') top no-repeat;}
.simcreator_comment-background{background:#f7ed95 url('http://www.flashflashrevolution.com/images/2008/comment-header.png') top no-repeat;}

#comment{
background: #000 !important;
color: #000065 !important;
width: 450px !important;
border: #000065 1px solid !important;
}

/*------------------------------------------------------------------
[16. Profile Menu / #profile_menu]
*/
#profile_menu ul{
	padding:0;
	margin:0;
	list-style:none;
	float:left;
	color:#000065 !important;
}
#profile_menu li{
	float:left;
	position:relative;
	text-align:left;
	color:#000065 !important;
	padding:3px 10px 2px;
	border-right:1px solid #46535d;
	background:url('http://i.imgur.com/pa4wD2H.png') repeat-x !important;
	min-height:15px;
	margin-top:1px;
}
#profile_menu li:hover,#profile_menu li.over{background:url('http://i.imgur.com/Gh8LP3N.png') repeat-x !important;}
#profile_menu li a:link,#profile_menu li a:hover,#profile_menu li a:active,#profile_menu li a:visited{color:#000065 !important}
#profile_menu li ul{
	display:none;
	position:absolute;
	top:1em;
	left:0;
	background:#000 !important;
	border:#48545f 1px solid;
}
#profile_menu li ul li{
	background:#000 !important;
	border:0;
	list-style-image:url('http://www.flashflashrevolution.com/images/2008/menubar-list-bullet.png') !important;
	list-style-position:inside;
	width:130px;
}
#profile_menu li ul li:hover,#profile_menu li ul li.over{
	background:#3E3E3E !important;
	color:#000065 !important;
	list-style-image:url('http://www.flashflashrevolution.com/images/2008/menubar-bullet-over.png') !important;
	list-style-position:inside;
}
#profile_menu li ul li a:link,#profile_menu li ul li a:hover,#profile_menu li ul li a:active,#profile_menu li ul li a:visited{color:#000065 !important;}
#profile_menu li>ul{top:19px;left:-1px;z-index:3;}
#profile_menu li:hover ul,#profile_menu  li.over ul{display:block;width:150px;height:auto;}
#profile_menu_wrapper {text-align:right;background:url('http://i.imgur.com/1SLVyE1.png') repeat-x !important;}
#profile_menu li ul li.menuDivider{
	list-style:none;
	list-style-image:none;
	color:#9c9ea1 !important;
	font-weight:bold;
}
#profile_menu li ul li.menuDivider:hover,#profile_menu li ul li.menuDivider.over{background:#000 !important;}

#commentTextarea{
background: #000;
color: #000065;
width: 450px;
border: #000065 1px solid;
}

/*------------------------------------------------------------------
[17. Tokens]
*/
.token-clear{clear:both;height:4px;}
.token-header{color:#595959 !important;padding-bottom:4px;display:block;}
.token-container-bg{width:175px;height:34px;padding:0px;border:1px solid #000 !important;margin-left:36px;}
.token-container{width:175px;height:34px;text-align:right;background:url('http://www.flashflashrevolution.com/images/token-css-test/token-bg_css.png');}
.token-container .info{float:left;}
.token-container .info img{padding:8px 2px 0 0;float:left;opacity:.75;filter: alpha(opacity=75); -moz-opacity: 0.75;}
.token-image{float:left;width:20px;height:30px;}
.token-container .info .count{float:left;width:30px;height:26px;background:#65a1d4;margin:0 4px 0 0;padding:8px 0 0 0;}
.token-container .info .count .text{color:#cddfee;}
.token-container .info .count .text strong{color:#fff;}
.token-container .info .text{padding:4px;white-space:nowrap;text-align:left;width:100px;overflow:hidden;}
.token-container #token-right .text{margin:0;padding: 20px 2px 0 0;color:#000;font-size:9px;}
.token-container #token-right object,.token-container #token-right embed,.token-container #token-right img{float:right;cursor:pointer;}

/*------------------------------------------------------------------
[18. Fixes]
*/
.line-fix{line-height:100%;}
.login-button-fix {margin:-3px 0 0 0;}
.video-fix{width:120px;}
.recently-played-fix {overflow:hidden;}
.comment-body{color:#000065 !important;}
#table2{color: #000065 !important;}
#textarea{
background: #000 !important;
color: #000065 !important;
width: 450px !important;
border: #000065 1px solid !important;
}
}
/*------------------------------------------------------------------
[19. Forums]
*/
@-moz-document url-prefix(http://www.flashflashrevolution.com/vbz/){
.page{background:#000 !important; color:#000065 !important;}
.alt1{background:#000 !important; color:#000065 !important;}
.alt2{background:#000 !important; color:#000065 !important;}
.alt1Active{background:#000 !important; color:#000065 !important;}
.panelsurround{background:#000 !important;}
.panel{background:#000 !important; color:#000065 !important;}
.vBulletin_editor{background:#000 !important; color:#000065 !important;}
.imagebutton {
    border:#000065 1px solid !important;
    padding:2px !important;
}
.tfoot{background:#000 !important;}
.smallfont{color:#000065 !important;}
.vbmenu_control{background:#000 !important; color:#000065 !important;}
.vbmenu_hilite{background:#3E3E3E !important; color:#000065 !important;}
.vbmenu_option{background:#000 !important; color:#000065 !important;}
.thead{background:#000 !important; color:#000065 !important;}
.tcat{background:#000 !important; color:#000065 !important;}
#vB_Editor_QR_textarea{
background: #000 !important;
color: #000065 !important;
width: 450px !important;
border: #000065 1px solid !important;
}
}


@-moz-document url-prefix(http://www.flashflashrevolution.com/tournament/){
