css:
@-moz-document domain(bitcoinwisdom.com) {

#leftbar {
        display: none;    
    }

#main {
	font-family: "Ubuntu Mono" ,monospace;
}

#sidebar_outer {
	font-family: "Ubuntu", monospace;
	font-size: 13px;
	line-height: 13px;
}

#leftbar {
        display: none;    
    }

.dark g {
	color: #727272;
}

table.simple { 
	border-collapse: collapse;
	font-size: 16px;
	font-weight: 400;
}

#main {
	font-family: "Ubuntu Mono" ,monospace;
}

.navbar .nav li.logo { 
	font-size: 10px;
	line-height: 12px;
}

.dropdown .t {
	padding: 2px 0px;
}

#gbids {
	width: 80px;
	color: #906060;
}

#gasks {
	width: 80px;
	color: #509040;
}


#bids .price {
	padding-left: 8px;
	color: #ff0000;
}

#asks .price {
	padding-left: 8px;
	color: #00ff00;
}

#bids .amount {
	padding-left: 8px;
	color: #ff0000;
}

#asks .amount {
	padding-left: 8px;
	color: #00ff00;
}

#bids .amount .g {
	padding-left: 8px;
	color: #cc0000;
}

#asks .amount .g {
	padding-left: 8px;
	color: #00cc00;
}

#sidebar #price { 
	font-size: 20px;
	font-weight: bold;
	height: 23px;
	line-height: 26px;
	text-align: center;
}

#orderbook #asks, #orderbook #gasks, #orderbook #bids, #orderbook #gbids { 
	display: inline-block;
	overflow: hidden;
	height: 208px;
	position: relative;
}

#orderbook .table .row { 
	height: 14px;
	line-height: 15px;
	margin: 0px;
	padding: 0px;
}

#orderbook .remove g {
	color: #777777;
}

#orderbook .remove { 
	color: #999999;
}

#control {
	font-size: 14px;
	font-weight: 700;
}

.navbar .nav li {
	display: inline-block;
	margin-right: 6px;
}

.navbar .nav { 
	list-style: none outside none;
	margin: 0px;
	padding: 0px;
}

.dark a.active { 
	color: #FFAA00;
	font-weight: bold;
}

.navbar {
	font-size: 12px;
}

}
