@-moz-document url-prefix("http://toster.ru/") {
body {
  margin: 0 auto;
  min-width: none;
  max-width: 960px;
}

.question_show > .info > .title {
  color: #666666 !important;
  font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}

.answers_block .answers_list .answers_list_item > .info > .meta .username a {
  color: #666666 !important;
}
        
.answers_block .answers_list .answers_list_item > .info > .meta .username a:hover {
  color: #333 !important;
}
        
.answers_block .answers_list .answers_list_item > .info > .meta .username {
    font-family: tahoma,sans-serif;
    font-size: 13px;
}
        
.answers_block .answers_list .answers_list_item > .info > .links_panel a.reply_answer_comment_link {
	color: #6DA3BD;  
    margin-left: 0px;
}
              
.answers_block .answers_list .answers_list_item > .info > .links_panel a.reply_answer_comment_link:hover {
	color: #4d7285;  
    margin-left: 0px;
}
          
.answers_block .answers_list .answers_list_item > .info > .links_panel a.reply_answer_comment_link:before {
	background-image: none !important;    
}
    
.answers_block .head .title {
	color: #666666 !important;
  	font: normal 15px Verdana, Tahoma, sans-serif !important;
}

.questions_list .questions_list_item .info .title a {
	color: #666666 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}

.questions_list .questions_list_item .info .title a:visited {
	color: #B5B5B5 !important;
  	font: 26px/118% normal Verdana, Tahoma, sans-serif !important;
}

.questions_list .questions_list_item .info .meta .answers_count {
	color: #339900 !important;
  	font-weight: normal !important;
}
          
.questions_list .questions_list_item .info .meta .views {
	color: #666666 !important;
}

.questions_list .questions_list_item .info .meta .author a {
	color: #666666 !important;
}

body .main_content .tags .tag > a {
	color: #666666 !important;
}

.questions_list .questions_list_item .info .meta .date {
	color: #666666 !important;
}

.question_show > .info > .meta .views {
	color: #666666 !important;
}

.question_show > .info > .meta .author a {
	color: #666666 !important;
}

.question_show > .info > .meta .date {
	color: #666666 !important;
}

body .main_content .tags .tag {
	border: 2px solid #B3B3B3;
}

.answers_block .answers_list .answers_list_item > .info > .links_panel a.reply_answer_comment_link .new {
	color: #339900;
  	font-weight: normal;
	font-size: 13px;
}

.question_interest_link {
	color: #666666 !important;
  	border: 3px solid #ACACAC;
}

.page_title {
	color: #4f6173 !important;
  	font: 26px/118% Verdana, Tahoma, sans-serif !important;
}

body .main_content .tabs .tab.open {
	color: #666666 !important;
  	font: bold 12px Verdana, Tahoma, sans-serif !important;
}

body .main_content .tabs .tab:hover {
	color: #666666 !important;
  	font: bold 12px Verdana, Tahoma, sans-serif !important;
}

body .main_content .tabs .tab {
	color: #888888 !important;
  	font: normal 12px Verdana, Tahoma, sans-serif !important;
}

.tags_list .tags_list_item .tagname a {
	color: #666666 !important;
  	font: 21px Verdana, Tahoma, sans-serif !important;
}

.tags_list .tags_list_item .meta .users_count b {
	color: #8798bc !important;
  	font: bold 13px Verdana, Tahoma, sans-serif !important;
}

.tags_list .tags_list_item .meta .questions_count b {
	color: #b690f3 !important;
  	font: bold 13px Verdana, Tahoma, sans-serif !important;
}

.right_sidebar .block > .title {
