}

.paginator a {
	color: #666666;
}
  
.empty_block .title {
	font: bold 16px Verdana, Tahoma, sans-serif !important;
}
  
.user_head .info .meta .rating b {
	font: bold 14px Verdana, Tahoma, sans-serif !important;
}
          
.user_head .info .meta .answers_count b {
