    font: bold 14px Verdana, Tahoma, sans-serif !important;
}

.answer_like_link.liked.has_like { 
	color: #4abfb4 !important;
    font: bold 14px Verdana, Tahoma, sans-serif !important;
}

.tracker_questions_list table tbody td .question_title a {
