css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#main-window #navigator-toolbox #nav-bar
{
	padding:3px 0 4px 0 !important;
}
#main-window #navigator-toolbox #toolbar-menubar
{
	background:none !important;
}



#main-window #navigator-toolbox #PersonalToolbar
{
-moz-appearance: none !important;

border: none !important;

background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;

color: white !important;
}



toolbox {
  -moz-appearance: none !important;
  background-color: #000000 !important;
  color: #F1F1FF !important;

}



toolbar .toolbarbutton-text,
#content .tab-text,
.menubar-text {
  color: #F1F1FF !important;
}

#content .tabs-container {
  -moz-appearance: none !important;
  background-color: #0E0E1A !important;
}


.tabbrowser-tab[selected="true"]

{
  -moz-appearance: none !important;
  background-color: #0A1013 !important;

}


statusbarpanel

{

-moz-appearance: none !important;

border: none !important;

background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;

color: white !important;

}

menubar, menubutton, menulist, menu, menuitem, toolbar, tab,
tree, tooltip /*,textbox*/
{
    font-family: Segoe UI, SegoeUI, calibri, verdana, helvetica, tahoma, Arial !important;
    font-size: 11px !important;
}
