@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asana.com") {

  /*condensed*/
  @font-face {
    font-family: 'proxima-nova-condensed';
    font-style: normal;
    font-weight: 600;
    src: local('Roboto Condensed Regular'),
    local('RobotoCondensed-Regular'),
    url(http://themes.googleusercontent.com/static/fonts/robotocondensed/v8/Zd2E9abXLFGSr9G3YK2MsO3ikv6SQm_yYbVf3nddkGw.woff) format('woff');
  }
  @font-face {
    font-family: 'proxima-nova-condensed';
    font-style: italic;
    font-weight: 700;
    src: local('Roboto Condensed Bold Italic'),
    local('RobotoCondensed-BoldItalic'),
    url(http://themes.googleusercontent.com/static/fonts/robotocondensed/v8/mg0cGfGRUERshzBlvqxeAGkmsVQraOiS9Ruw9APgQZo.woff) format('woff');
  }
  /*regular*/
  @font-face {
    font-family: 'proxima-nova';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto Regular'),
    local('Roboto-Regular'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/Xyjz-jNkfiYuJf8UC3Lizw.woff) format('woff');
  }
  @font-face {
    font-family: 'proxima-nova';
    font-style: normal;
    font-weight: 500;
    src: local('Roboto Medium'),
    local('Roboto-Medium'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/oOeFwZNlrTefzLYmlVV1UD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  }
  @font-face {
    font-family: 'proxima-nova';
    font-style: normal;
    font-weight: 600;
    src: local('Roboto Medium'),
    local('Roboto-Medium'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/oOeFwZNlrTefzLYmlVV1UD8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
  }
  /*italics*/
  @font-face {
    font-family: 'proxima-nova';
    font-style: italic;
    font-weight: 400;
    src: local('Roboto Italic'),
    local('Roboto-Italic'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/dFWsweFqlD8ExfyN7Gh_GPesZW2xOQ-xsNqO47m55DA.woff) format('woff');
  }
  @font-face {
    font-family: 'proxima-nova';
    font-style: italic;
    font-weight: 500;
    src: local('Roboto Medium Italic'),
    local('Roboto-MediumItalic'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/OLffGBTaF0XFOW1gnuHF0dIh4imgI8P11RFo6YPCPC0.woff) format('woff');
  }
  @font-face {
    font-family: 'proxima-nova';
    font-style: italic;
    font-weight: 700;
    src: local('Roboto Bold Italic'),
    local('Roboto-BoldItalic'),
    url(http://themes.googleusercontent.com/static/fonts/roboto/v9/t6Nd4cfPRhZP44Q5QAjcC9Ih4imgI8P11RFo6YPCPC0.woff) format('woff');
  }
}
