css:
@-moz-document url-prefix("http://archiveofourown.org"), url-prefix("https://archiveofourown.org") {
dl.stats a:link { color: #ffffff !important;}
dl.stats a:visited { color: #ffffff !important;}
dl.stats a:hover { color: #ffffff !important;}
dl.stats a:active { color: #ffffff !important;}
}

@-moz-document url-prefix("http://archiveofourown.org/works"), url-prefix("http://archiveofourown.org/chapters"), url-prefix("https://archiveofourown.org/chapters"), url-prefix("https://archiveofourown.org/works") {
dl.stats { display: none !important}
.kudos { display: none !important;}
}
