css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rubydoc.info"),
               domain("ruby-doc.org") {

.class #documentation,
.file #documentation,
.module #documentation {
    max-width: 100% !important;
}

ul.link-list .type {
   color: #9854d4 !important;
}

.discussion p,
.method-detail p {
   font-family: 'palatino linotype', palatino, serif !important;
   font-size: 16px !important;
}

div#class_method_details,
ul.summary {
  margin-left: 5px !important;
  padding-left: 5px !important;
  margin-right: 15px !important;
  padding-right: 15px !important;
}

/* pre, */
.method-detail pre,
ul.summary pre,
div#class_method_details pre {      
        white-space: pre !important;
	white-space: pre-wrap !important;
	white-space: -moz-pre-wrap !important;
	white-space: -pre-wrap !important;
	white-space: -o-pre-wrap !important;
	word-wrap: break-word !important;
}


ul.summary li.public {
   margin-top: .75em !important;
   margin-bottom: .75em !important;
}

tt {
   font-family: monospace !important;
}
p tt {
   color: #111 !important;
  font-weight: 600 !important;
}

ul#full_list,
ul#full_list li,
ul#full_list li span,
ul#full_list li span a,
ul#file-metadata,
ul#file-metadata a,
ul#files,
ul#files a,
ul.link-list,
ul.link-list a {
   font-family: arial !important;
   font-size: 12px !important;
}

}
