css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
 domain("login.live.com") {
   /* default colors:
      fgcolor=#797979 overall fg
      bgcolor=#232323 overall bg
      bdcolor=#555555 selected menu item bgcolor
      IBfgcolor=#444444 selected message bg
      MLbdcolor=#333333 hovered menu item(or msg) bgcolor
      IF2fgcolor=#191919 button bg
      RPbgcolor=#252525 right panel default bgcolor
      Lfgcolor=#999999 lightforground?
      Obgcolor=#292929 other light-darkgrey background?
   */

	 
   #iUser0,
   #iUser0 *,
   .cssAppBigBold,
   #i1670,
   #mainTD,
   #footerTD,
   #footerTD *,
   #leftTD,
   #leftTD *,
   body,
   #idRemoveMeLink09,
   td#productTD * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[RPbgcolor]]*/ !important ; background-image: none !important ; border-color: /*[[bdcolor]]*/ !important }
   td#productTD img { visibility: hidden !important }

   .cssFooterPadding { padding: 0 !important ; border-width: 0 !important}

   #i2036,
   #cbImgTD,
   #GradientDiv,
   .cAdBannerContainer { display: none !important }
   td#rightTD tr > td.cssUserBtnHoverR3 { background-color: /*[[MLbdcolor]]*/ !important ; border: 1px solid #222222 !important }
   td#rightTD tr > td.cssUserBtnHoverR3 a.cssHiddenLink { color: /*[[fgcolor]]*/ !important }
}

@-moz-document 
 domain("mail.live.com") {
   html, body, ul, table { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important; }
   * { border-color: /*[[bdcolor]]*/ !important }
   div.LoadingIndicator *,
   div.AttBox, div.AttBox * { color: /*[[IF2fgcolor]]*/ !important ; background-color: /*[[IBfgcolor]]*/ !important }

   div.SideAds,
   div#CustComm_120x60,
   div#SkyscraperContent * { display: none !important }

   textarea,
   input,
   label { color: /*[[fgcolor]]*/ !important ; border-width: 0 !important ; background-color: /*[[bgcolor]]*/ !important }

   /* hidden drop down menu */
   ul.c_m li { margin: 2px !important ; border: 1px solid /*[[fgcolor]]*/ !important }
   ul.c_m li a.sel { color: /*[[IF2fgcolor]]*/ !important ; background-color: /*[[IBfgcolor]]*/ !important }
   div#contextMenu li:hover *,
   ul.c_m li:hover * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }

   /* info box */
   div.LoadingIndicator { color: /*[[IF2fgcolor]]*/ !important ;background-color: /*[[fgcolor]]*/ !important ; border: 1px solid /*[[bdcolor]]*/ !important }
   div.InfoPaneInner { background-color: /*[[fgcolor]]*/ !important }
   div.InfoPaneInner span{ color: /*[[IF2fgcolor]]*/ !important ;background-color: /*[[fgcolor]]*/ !important }
   div.InfoPaneInner a { color: /*[[IF2fgcolor]]*/ !important ; text-decoration: underline !important }

   /* Reading pane settings page */
   table#mainLayoutTable li { border-width: 1px !important ; margin: 2px !important }
   table#mainLayoutTable li a { border-width: 0 !important ; color: /*[[fgcolor]]*/ !important }
   table#mainLayoutTable li:hover a { background-color: /*[[MLbdcolor]]*/ !important }
   div.ToolsCustomerCommunication { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important; }

   /* top pannel */
   div#HeaderContainer div#RadAd_Banner,
   div#HeaderContainer div#RadAd_TodayPage_Banner { display: none !important }
   div#HeaderContainer div#c_header *,
   div#titleSearchContainer,
   div#titleSearchContainer *,
   div#sortFilterContainer,
   .TableHeaderGradient ,
   div#mainContent { color: /*[[fgcolor]]*/ !important ; background-image: none !important ; background-color:  /*[[bgcolor]]*/ !important; }
   div#HeaderContainer div.c_hg { padding-bottom: 0 !important }
   div#HeaderContainer form#c_search,
   div#HeaderContainer span#c_metile { display: none !important }


   div.SoftShadows div,
   div#mpf0_details, div#mpf0_details * { background-image: none !important }
   
   /* left pannel */
   div#contentLeft div { background-color: /*[[bgcolor]]*/ !important }
   div#contentLeft li { border-width: 0 !important ; margin: 0 0 2px 0 !important ; color: /*[[fgcolor]]*/ !important }
   div#contentLeft li a { color: /*[[fgcolor]]*/ !important }
   div#contentLeft li a:hover { background-color: /*[[MLbdcolor]]*/ !important }
   div#contentLeft li.FolderItemSelected a { color: /*[[IF2fgcolor]]*/ !important ; background-color: /*[[bdcolor]]*/ !important }
   div#contentLeft li.FolderItemSelected a:hover { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }

   div#contentLeft a.ManageLink,
   div#contentLeft span.AddAccount a { margin: 10px 0 !important ; font-weight: bold !important ; text-decoration: underline !important ; color: /*[[fgcolor]]*/ !important }
   div#contentLeft a.ManageLink:hover,
   div#contentLeft span.AddAccount:hover { background: /*[[MLbdcolor]]*/ !important }

   div#contentLeft div.MiniFooterContainer { padding-left: 0.8em !important }
   div#contentLeft div.MiniFooterContainer:hover { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }

   div.FlyoutFooter table { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }
   div.FlyoutFooter table * { color: /*[[fgcolor]]*/ !important }


   /* message sent */
   div.SmcHeaderContainer { background-color: /*[[RPbgcolor]]*/ !important }

   /* right pannel */
   div#MainContent { background-color: /*[[RPbgcolor]]*/ !important }
   div#MainContent table.ManageFoldersTable thead th { border-color: /*[[bdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; background-color: /*[[IF2fgcolor]]*/ !important }
   div#MainContent table.ManageFoldersTable tbody * { color: /*[[fgcolor]]*/ !important ; background: none #323232 !important }
   div#MainContent table.ManageFoldersTable tbody tr:hover * { color: /*[[fgcolor]]*/ !important ; background: none /*[[IBfgcolor]]*/ !important }

   div#contentRight div.TodayContentContainer div.Upsell,
   div#contentRight div.TodayRightContentContainer div.c_hf { background-color: /*[[bgcolor]]*/ !important ; border-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div.TodayContentContainer div.StorageMeterBar { background-color: /*[[IBfgcolor]]*/ !important ; border-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div.TodayContentContainer div.StorageMeterBar div.Bar { border-color: #999999 !important }
   div#contentRight div.TodayOptOut a { color: /*[[fgcolor]]*/ !important ; text-decoration: underline !important }
   div#contentRight div#toolbarContainer li a { border-width: 0 !important ; color: /*[[fgcolor]]*/ !important }

   div#contentRight div#safetyBar { border-color: #FFFACD !important }

   div#contentRight div#MainContent div#sortSearchContainer { border-color: /*[[MLbdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; background: none /*[[IF2fgcolor]]*/ !important }
   div#contentRight div#MainContent div#sortSearchContainer span { color: /*[[fgcolor]]*/ !important }
   div#contentRight div#MainContent div#sortSearchContainer li.ProductSearchBox div.c_hf { padding: 0 !important ; border-width: 0 !important ; background-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div#MainContent div#sortSearchContainer li.ProductSearchBox div.c_hf input { border-color: /*[[fgcolor]]*/ !important }

   div#contentRight div#MainContent div#messageListSplitPane { border-color: /*[[MLbdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div#MainContent div.MessageListItems { margin: 0 !important ; border-color: /*[[bgcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }
   div#contentRight div#MainContent div.MessageListItems * { color: /*[[fgcolor]]*/ !important }
   div#contentRight div#MainContent div.MessageListItems tbody td { border-color: /*[[MLbdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important }
   div#contentRight div#MainContent div.MessageListItems tbody tr:hover * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div#MainContent div.MessageListItems tr.MessageListItemSelected * { color: /*[[IF2fgcolor]]*/ !important ; background-color: /*[[IBfgcolor]]*/ !important }

   div#contentRight div#MainContent div.PaginationContainer { color: /*[[fgcolor]]*/ !important ; border-color: /*[[MLbdcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }
   div#contentRight div#MainContent div.PaginationContainer ul li a { color: /*[[fgcolor]]*/ !important }
   div#contentRight div#MainContent div.PaginationContainer ul li { color: #999999 !important }

   div#contentRight div#readingPaneSplitPane { color: /*[[fgcolor]]*/ !important ; background-color: /*[[Obgcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div#actionBar { border-width: 0 !important }
   div#contentRight div#readingPaneSplitPane div#actionBar li { margin: 3px 3px 0 3px !important ; padding: 3px 8px 4px 8px !important ; border: 1px solid /*[[bdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; background: none /*[[IF2fgcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div#actionBar a { border-width: 0 !important ; padding: 0 !important }
   div#contentRight div#readingPaneSplitPane div#actionBar span { background-color: /*[[IF2fgcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div#actionBar li:hover { background: none /*[[MLbdcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div#actionBar li:hover span { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }

   div#contentRight div#readingPaneSplitPane * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[Obgcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div#readingPaneContainer { border-top: 1px !important ; margin-top: 4px !important }
   div#contentRight div#readingPaneSplitPane div#readingPaneContainer a { color: /*[[fgcolor]]*/ !important ; text-decoration: underline !important }
   div#contentRight div#readingPaneSplitPane div.ReadMsgHeader { margin-top: 1em !important ; padding-left: 1em !important ; border: 1px solid /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }

   div#contentRight div#readingPaneSplitPane div.ReadMsgHeader * { background-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div#readingPaneSplitPane div.ReadMsgBody { border-width: 0 !important }

   div#contentRight div#MainContent div.Compose { margin: 5px !important ; border-width: 1px !important }
   div#contentRight label,
   div#contentRight li * { color: /*[[fgcolor]]*/ !important }


   div#contentRight div#composeHeader input { border-width: 0 !important }
   div#contentRight div#composeHeader span#toBoxTo div div,
   div#contentRight div#composeHeader li.Block,
   div#contentRight div#composeHeader div { background-color: /*[[bgcolor]]*/ !important }
   /*div#contentRight div#composeHeader div.InputDivToSubject { background-color: /*[[bgcolor]]*/ !important }*/


   div#contentRight div#composeHeader div.ACShadow table.ResultsTable tr:hover { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }
   div#contentRight div#composeHeader div.ACShadow table.ResultsTable tr:hover td * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[MLbdcolor]]*/ !important }


   div#contentRight div#composeMessage div.ButtonList { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important } 
   div#contentRight div#composeMessage div.ButtonList * { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important } 

   div#contentRight div#composeMessage div#RichTextEditor div.Container { background-color: /*[[MLbdcolor]]*/ !important ; color: /*[[fgcolor]]*/ !important ; margin: 0 !important } 
   
   /* Contacts */

   div#MainContent div.Pagination { color: /*[[fgcolor]]*/ !important ; border-color: /*[[MLbdcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }
   div#MainContent div.Pagination ul li a { color: /*[[fgcolor]]*/ !important }
   div#MainContent div.Pagination ul li { color: #999999 !important }

   div.ContentLeft a.DisplayBlock { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }

   div#MainContent table.ItemListContentTable th  { color: /*[[fgcolor]]*/ !important ; background-color: /*[[bgcolor]]*/ !important }
   div#MainContent table.ItemListContentTable td { color: /*[[fgcolor]]*/ !important ; background-color: /*[[Obgcolor]]*/ !important }
   div#MainContent table.ItemListContentTable span { color: /*[[fgcolor]]*/ !important ; background-color: /*[[Obgcolor]]*/ !important }
   div#MainContent table.ItemListContentTable div.cxp_ic_highlight { color: /*[[fgcolor]]*/ !important ; background-color: /*[[Obgcolor]]*/ !important }

}

/* text color when editing a message */
@-moz-document url("about:blank") {
body[qatagid="qa.mail.compose.body.firefox"] {
color: /*[[fgcolor]]*/ !important;
}
}
