@-moz-document regexp("htt(p|ps)://.*\.facebook\.com/.*") {

  .home {
    overflow: hidden;
  }
  
  /* display: none; and some JS somewhere constantly pulls back more data */
  div[id*="topnews_main_stream"] {
    visibility: hidden;
  }
  
  div[id*="ego_pane"],
  div.ego_column {
  
  display: none;
  }

  #pagelet_trending_tags_and_topics {
  display: none;
  }

  div#groupsNav ~ div[id$="Nav"] { 
  display: none;
  }
}
