css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {
    body {
        background: #000;
        color: #ccc;
    }
    #searchText, #launcher {
        background-color: #111 !important;
    }
    .launchButton:hover {
        background-color: #222 !important;
    }
    #searchSubmit {
        background-image: linear-gradient(hsla(0,0%,50%,.8), hsla(0,0%,50%,.1));
    }
    #searchText, #snippets, .launchButton {
        color: #ddd !important;
    }
    .searchSuggestionTable {
        background-color: #222;
    }
    input {
        box-shadow: none !important;
    }
}
