	}
	
	div#logo_bereich{
		display: none !important
	}
	
	#navi_bottom{
		display: none !important
	}
	
	#navi_top{
		text-align:left !important;
	}
	
	.position_1{
		float:none !important;
		clear:both !important;
		width:700px !important;
		margin:0  !important;
	}
	
