css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com") {

html {min-height:100%;}

body.fbIndex {background: url("http://images6.fanpop.com/image/photos/32400000/Sword-Art-Online-sword-art-online-32494406-1500-1071.jpg") 50% 50% no-repeat !important;
background-size: cover !important;
height:100%;
overflow:hidden;}

.loggedout_menubar_container {background:none !important;}
.loggedout_menubar label, .login_form_label_field a {color:#fff !important;}
.loggedout_menubar {background:#FFD186 !important;
padding:0 5px 5px 5px;
-webkit-border-radius: 50px;
border-radius: 500px;
-webkit-box-shadow:  0px 0px 2px  rgba(0, 0, 0, .4);
box-shadow:  0px 0px 2px  rgba(0, 0, 0, .4);}

.fbIndex #globalContainer #dropmenu_container,.fbIndex #globalContainer #content,.fbIndex #globalContainer #pageFooter {display: none !important}
.fbIndex .loggedout_menubar_container {position: fixed !important;
width: 800px !important;
height: 82px !important;
min-width: 0 !important;
top: 13% !important;
left: 50% !important;
margin: -60px 0 0 -210px !important;
z-index: -1 !important;}
.fbIndex .loggedout_menubar {position: fixed !important;
width: 800px !important;
height: 140px !important;
min-width: 0 !important;
top: 13% !important;
left: 50% !important;
margin: -60px 0 0 -410px !important;
z-index: -1 !important;}
.fbIndex .loggedout_menubar_container .lfloat,.fbIndex .loggedout_menubar_container .rfloat {float: none !important;}
.fbIndex .loggedout_menubar_container .lfloat img,.fbIndex .loggedout_menubar_container .rfloat #login_form table {display: block !important;
margin: 5px 0 4px 198px !important;}
.fbIndex .loggedout_menubar_container .lfloat img {display: block;
margin: -60px auto 200px !important;}

#SetAsHomepage_Callout {display: none;}

.fb_logo {background:url("data:image/svg+xml;base64,") 50% 50% no-repeat !important;
background-size:130% auto !important;
width: 200px;
margin: 14px 0 4px 298px !important;}

.menu_login_container .inputtext, .menu_login_container .inputpassword {border-color: #000000 !important;}

.uiButtonConfirm {background-color: #884153!important;
background-image: none!important;
border-color: #884153!important;
border-radius:10px!important;}

#email,#pass{border-color:#884153!important;
background:#fff !important;
font-family: 'Lucida Console',sans-serif,monospace,sans-serif,arial !important;
border-radius:10px!important;}

}
