css:
@-moz-document domain('imgur.com') {
#right-content{display: none !important;}
#submit-caption{display: none !important;}
#side-gallery{display: none !important;}
#footer-links{display: none !important;}
#scrollpanel{display: none !important;}
#topbar{display: none !important;}
.image-arrows{display: none !important;}
.favorite-icon{display: none !important;}
.info{display: none !important;}
.report-icon{display: none !important;}
.comments-info{display: none !important;}
.expand{display: none !important;}
.no-expand{display: none !important;}
.arrows{display: none !important;}
.caption-toolbar{display: none !important;}
.comment:nth-child(3) ~ .comment{display: none !important;}
.bad-captions{display: none !important;}

#content.two-column{width:100% !important;}
.main-image{width: 99% !important; margin-left:5px !important;}
.panel{width: 97.5% !important;position:absolute !important;padding:20px !important;}
.left.main{padding-left:5px !important;}
.image{width:100% !important;}
#image{width:80% !important;}
#under-image{ position: absolute !important; right:0 !important; top: 35px !important; padding-right:5px !important;width:19% !important;}
div img{max-width: 100% !important;height:92vh !important;}
.jquery-image-zoom img,.post > a > img,img[id^="thumb"], img[src$="loader.gif"]{height:auto !important;}
}
