css:
@-moz-document domain("tvtorrents.com") {

body {
background:#ddd url("http://img20.imageshack.us/img20/9724/a6vr.png") center 0px no-repeat !important;
margin-top:120px !important;
color: black !important;
}

html a {
color:#001 !important;
}

html a:hover {
color: #31538a !important;
}

div.container{
margin:0 auto !important;
padding: 0 !important;
width: 1010px !important;
}

div.left{
margin-right: 5px !important;
}

div.middle{
width: 800px !important;
position: relative !important;
float: right !important;
left: 0 !important;
margin: 0 !important;
}

div.right {
display: none !important;
}

.menu {
background: -moz-linear-gradient(#628fda, #4e71ac) !important;
background: -webkit-linear-gradient(#628fda, #4e71ac) !important;
background: -o-linear-gradient(#628fda, #4e71ac) !important;
background: linear-gradient(#628fda, #4e71ac) !important;
color: #fff !important;
}

.menu > div > form > .tinput {
    position: relative !important;
    left: 530px !important;
    top: -37px !important;
}

.menu > div > form > .miscbutton2 {
    position: relative !important;
    left: 335px !important;
    top: -12px !important;
}

.menu > div[style="float:right;"] {
    position: relative !important;
    top: 4px !important;
    left: -10px !important;
}

.menu a {
    color: #fff !important;
    text-shadow: 2px 2px 2px #31538a !important;
    position: relative !important;
    top: 6px !important;
}

.menu a:hover {
    color: #fff !important;
    text-shadow: 2px 2px 2px #132036 !important;
}

.menu > div[style="float:right;"] > a {
    text-shadow: none !important;
    top: 0 !important;
}

.menuIcon {
    position: relative !important;
    top: 8px !important;
}

.head {
background: -moz-linear-gradient(#628fda, #4e71ac) !important;
background: -webkit-linear-gradient(#628fda, #4e71ac) !important;
background: -o-linear-gradient(#628fda, #4e71ac) !important;
background: linear-gradient(#628fda, #4e71ac) !important;
text-shadow: 1px 1px 1px #132036 !important;
font-size: 14px !important;
}

font.head {
background: none !important;
text-shadow: none !important;
}

.body {
background:#eee !important;
}

.sidebox {
    position: static !important;
	left: 205px !important;
	width:100% !important;
	margin:0 0 10px 0 !important;
	margin-right:5px !important;
	background-color: #eee !important;
	background-image: none !important;
	box-shadow: 0px 0px 2px 0px #888 !important;
}

.subbox {
	background-image: none !important;
	background-color: #a9c4f1 !important;
	padding: 3px !important;
	font-size: 12px !important;
}

.subbox a {
	font-size: 12px !important;
}

.subbox a:hover {
	color: #fff !important;
}

div.subbox a[style="background-color: rgb(17, 34, 170);"], .subbox a[style="background-color:#1122AA;"] {
color: white !important;
background: none !important;
}

table {
    border-collapse: collapse !important;
}

.rub { /*        TABLE HEAD         */
    background-color: #4e71ac !important;
}

.list_even {
    background-color: #c6d6f1 !important;
}

.list_even:nth-child(odd) {
    background-color: #d4dff1 !important;
}

tr[onmouseover="this.oldcolor=this.bgColor; this.bgColor='#CACDEE'"]:nth-child(4n+1) {
    background-color: transparent !important;
}

tr[onmouseover="this.oldcolor=this.bgColor; this.bgColor='#CACDEE'"]:nth-child(4n+3) {
    background-color: #d4dff1 !important;
}


td[style="background-color:#CCCCCC;"] {
    background-color: rgb(189,210,244) !important;
}

.boxhead {
    background: -moz-linear-gradient(#628fda, #4e71ac) !important;
    background: -webkit-linear-gradient(#628fda, #4e71ac) !important;
    background: -o-linear-gradient(#628fda, #4e71ac) !important;
    background: linear-gradient(#628fda, #4e71ac) !important;
    text-shadow: 2px 2px 1px rgb(17,52,111) !important;
    font-size: 12px !important;
    
}

.boxbody {
    background: none !important;
    padding: 5px !important;
}

div[style="overflow:auto; height:90px;"] {
    height: auto !important;
}

td[style="background-color:#1122AA; color:#FFFFFF; padding:2px;"] {
    background-color: #4e71ac !important;
}

tr.schedule {
    background-color: #c6d6f1 !important;
}

tr.schedule:nth-child(odd) {
    background-color: #d4dff1 !important;
}

td.schedule, a.schedule {
    background-color: inherit !important;
}

tr[style="background-color: #55CC55;"]
{
    background-color: #79c779 !important;
}

tr[style="background-color: #AAADCE;"]
{
    background-color: #c7c779 !important;
}

.schedule_fav
{
    background-color: inherit !important;
    color: #000 !important;
}

div[style="overflow: hidden; padding:5 5 5 5; vertical-align:top; font-size: 16px; background-color: #cccccc;"] {
    background-color: #bdd2f4 !important;
}

#add_tag {
    left: -159px !important;
    top: 0px !important;
}

h2 {
    background: none !important;
}

.tinput {
    border: 1px solid #abadb3 !important;
    width: 184px !important;
    background-color: #fff !important;
}

.tinput[type="text"] {
    padding: 3px !important;
}

select.tinput {
    width: 190px !important;
}

.miscbutton2 {
    border: 1px solid #abadb3 !important;
    height: 24px !important;
    color: #333 !important;
    background-color: #fff !important;
}

div.left .miscbutton2 {
    position: relative !important;
    left: -5px !important;
    width: 60px !important;
}

div.left .sidebox .miscbutton2 {
    left: 0px !important;
}

.combo_select, .combo_input {
    border: 1px solid #abadb3 !important;
}

.tinput:hover, .miscbutton2:hover, .combo_select:hover, .combo_input:hover {
    border-color: #7eb4ea !important;
}

div.container > table{
display:none;
}

.subbox > img {
    display:none !important;
}

#tvtbannerImg {
    display: none !important;
}

.tvtbox {
border: none !important;
border-radius: 0 !important;
box-shadow: none !important;
margin: 0 0 10px 0 !important;
box-shadow: 0px 0px 2px 0px #888 !important;
}

#dl_frame {
display:none !important;
}



}
