@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("heise.de") {

/* Inhalt */
#container {width: 100% !important; margin: 0 auto !important; top: -100px;}
#container_content {width: 100% !important;}
#mitte {width: 100% !important; background: #fff !important;}
#mitte_links {width: 76% !important; position: relative;} 
#mitte_rechts {width: 24% !important; background: #eee !important; overflow: visible !important;}
#mitte #mitte_rechts .col50anschnitt {width: 40% !important;}
.col50 {width: 40% !important; padding: 0 !important;}
.news_logo {position: absolute; right: 10em;}
.col100weiss, .col100aktion, .col100 {width: 90% !important;}

/* Werbung ausblenden (was Adblock nicht schafft) */
.col50preisvergleich, .online-markt, .col100preisvergleich, #bannerzone, .adbottom, .bcadv, .cadv {display: none !important;}

}
