css:
@-moz-document url-prefix(http://www.nicovideo.jp/ranking){

/*-------------*
 * RANK NO.
 *-------------*/
div.rankingNumWrap{
    width: 60px!important;
    padding-top:0!important;
    font-family: inherit!important;
    font-weight: bold!important;
}

p.rankingNum{
    font-size: 20px!important;
    top:0!important;
    padding-right:5px!important;
    color: rgb(41, 47, 47)!important;
    float:right;
}
p.rankingNum:after{
    content:'位';
    vertical-align: text-top;
    font-size:12px;
}

p.rankingPt{
    position:absolute!important;
    top:8px!important;
    left:192px!important;
    font-size: 16px!important;
    color: rgb(41, 47, 47)!important;
}

/*-------------*
 * THUMB IAMGE
 *-------------*/
div.videoList01Wrap{
    width:120px!important;
}

div.itemThumbBox{
    position:relative!important;
    width:120px!important;
}
    
.videoRanking div.itemThumb{
    position:absolute!important;
    top:0!important;
    left:-20px;
    width:120px!important;
}

.video .itemThumb img{
    margin-top:0!important;
    margin-left:0!important;
    width:120px!important;
}

/*-------------*
 * TIME
 *-------------*/
p.itemTime{
    position:relative!important;
    top:-2px!important;
    left:220px!important;
    font-weight: 900!important;
    font-size: 12px!important;
    width:200px!important;
}

p.itemTime.new{
    color:red!important;
}

/*-------------*
 * RANK ITEM
 *-------------*/
li.item.videoRanking{
    border: solid 2px grey !important;
    margin: 2px!important;
}

/*-------------*
 * COUNT VALUES
 *-------------*/
li.count{
    color: rgb(41, 47, 47)!important;
}

span.value{
    font-weight: 900!important;
}

li.count.comment, li.count.mylist, li.count.ads{
    border-left: 1px black solid!important;
    padding-left: 6px!important;
}



/*-------------*
 * ADS - RANK NO.
 *-------------*/
.sub .ranking div.uad .item{
    position:relative!important;
    height: 65px!important;
    font-weight: bold!important;
}

.sub .ranking div.uad .item .num,
.sub .ranking div.uad .item .count{
    position: absolute!important;
    color: rgb(41, 47, 47)!important;
    font-size: 11px!important;
    font-family: inherit!important;
}

.sub .ranking div.uad .item .num{
    width: 100px!important;
    left: 60px;
}
.sub .ranking div.uad .item .count{
    top: 0px!important;
    left: 120px;
}
.sub .ranking div.uad .item .count:before{content:'位('}
.sub .ranking div.uad .item .count:after{content:')'}

.sub .ranking div.uad .item .itemTitle{
    position: absolute!important;
    top: 18px;
}

.sub .ranking div.uad .item .countAll{
    position: absolute!important;
    top: 50px;
}

}
