css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ssl.scroogle.org") {

body
{
font-family:arial,sans-serif !important;
font-weight:bold !important;
margin-left:0 !important;
margin-right:0 !important;
padding-left:1% !important;
padding-right:1% !important;
padding-top:26px !important;
}

blockquote
{
margin:0 !important;
padding:0 !important;
margin-top:0px !important;
color:#FFFFFF !important;
}

blockquote b
{
color:#000000 !important;
}

blockquote a
{
display:block !important;
margin-top:-20px !important;
background-color:#FFFFFF !important;
z-index:999;
color:#0000FF !important;
font-weight:normal !important;
width: 535px !important;
}

blockquote a:link b,
blockquote a:hover b
{
color:#0000FF !important;

}

blockquote a:visited b,
blockquote a:active b
{
color:#551A8B !important;
}

form
{
margin:0 !important;
padding:0 !important;
padding-bottom:18px !important;
border-bottom: 1px solid #6B90DA !important;
z-index:1 !important;
}

center
{
text-align:left !important;
padding-bottom:30px !important;
border-bottom: 24px solid #EBEFF9 !important;
margin-bottom:-22px !important;
font-weight:normal !important;
color:#000000 !important;
}

center form
{
display:none !important; 
}

ul
{
margin:0 !important;
padding:0 !important;
margin-bottom:20px !important;
line-height:16px !important;
font-weight:normal !important;
color:#000000 !important;
width: 535px !important;
overflow-x:hidden !important;
}

* + center
{
background-image: url(data:image/gif;base64,) !important;
background-repeat:no-repeat !important;
background-position: 0px 14px !important;
background-color:#FFFFFF !important;
position: absolute !important;
top:0 !important;
left:1% !important;
width: 98% !important;
border:0 !important;
padding-top: 10px !important;
padding-bottom: 0px !important;
}

* + center form
{
display:block !important;
margin-top: 20px !important;
margin-bottom:0 !important;
padding-bottom:8px !important;
}

* + center input[type="text"]
{
margin-left:77px !important;
width: 350px !important;
}

* + center a
{
display: none !important;
}

* + center br
{
display: none !important;
}

/* Ensure that the radio-buttons for numbers are on their own line */
* + center font
{
display: block !important;
clear: both !important;
padding-left: 77px !important;
}

/* Make the Number-Word Capital */
* + center font:first-letter
{
text-transform:uppercase !important;
}

}
