#header, #crumbs, #sidebar, #cat_description, #social-circle, #footer, .thumbnail-wrap .comments, .share-container, .sharing, #subscribe, #nav_wrap, #quest {display:none!important;}

* {border-radius:0!important;}
h1, h2 {padding-bottom:10px!important;}
#page {margin-top:70px!important;}
#wrapper_b, #wrapper_t {background:#FFF!important;}
.meniu-slim {display:block!important; background:#f8f8f8!important; opacity:1!important;}
.design_ideas {border:none!important;}

}

/* --------------------------------------------- #Youtube --- */
@-moz-document domain("youtube.com") {
#alerts, #appbar-guide-menu {display:none!important;}
.guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content {  padding-left: 0px!important;}
.flex-width-enabled.flex-width-enabled-snap .content-alignment {
width: 90%!important;}
}

/* --------------------------------------------- #Netflix--- */
@-moz-document domain("netflix.com") {
#uma {display:none!important;}
}

/* --------------------------------------------- #Pinterest--- */
@-moz-document domain("pinterest.com") {
* {border-radius:0!important;}
}
