css:
@-moz-document url("about:newtab") {

#newtab-scrollbox:not([page-disabled]), 
#newtab-scrollbox { 
    background: -moz-linear-gradient(top, #000000, #303030 50%, #000000, #303030 50%, #000000) !important;
    -webkit-background-size: cover !important;
    -o-background-size: cover !important; 
    background-size: cover !important;  
    overflow-y: hidden !important; 
    min-height: 100% !important; 
    min-width: 1024px !important; 
    width: 100% !important; 
    height: auto !important;
}
.newtab-thumbnail {
    background-position: center !important;
    -webkit-background-size: cover !important;
    -o-background-size: cover !important; 
    background-size: cover !important;
    background-size: 100% 156px !important;
    opacity: 0.2 !important;  
    -khtml-opacity: 0.2 !important; 
    max-height:  86% !important;
    border-radius: 12px 12px 0px 0px !important;
}
.newtab-thumbnail:hover { 
    background-position: center !important; 
    -webkit-background-size: cover !important;
    -o-background-size: cover !important; 
    background-size: cover !important;
    background-size: 100% 156px !important;
    opacity: 1.0 !important; 
    -khtml-opacity: 1.0 !important;   
    -moz-transition: all 0.2s ease-out 0s !important; 
    -webkit-transition: all 0.2s ease-out 0s !important; 
    -o-transition: all 0.2s ease-out 0s !important;  
    max-height:  86% !important;
}
#newtab-window .newtab-cell,
#newtab-window .newtab-site,
#newtab-window .newtab-site:hover,
#newtab-window .newtab-cell:hover {
    background-color: transparent !important;
    border-radius: 12px !important;
}
.newtab-cell {
    border: 1px solid black !important;
}
.newtab-cell:hover {
    box-shadow: 0 0 16px rgb(0, 170, 255) !important;
}
#newtab-window a.newtab-link {
    background-position: center !important;
    border-radius: 12px !important;
}
.newtab-title {
    background: -moz-linear-gradient(center top , rgb(48, 48, 48), rgb(0, 0, 0)) repeat scroll 0% 0% transparent !important;
    text-align: center !important; 
    text-shadow: #000000 0px 0px 3px !important;
    border-radius: 0px 0px 12px 12px !important;
}
a {
    outline-style: none !important;
    text-decoration: none !important;
}
}
