css:
@-moz-document domain("www.nexusmods.com") {
#top .sub-bar .search {
  	border: 0;
}

.search a {
  	visibility: hidden;
}

.search .bubble-box {
  	display: block !important;
  	background: none;
 	border: none;
  	box-shadow: none;
}

.search .bubble-box .heading, .search .bubble-box .bubble-arrow, .search .bubble-box .arrowtop-border, .search .bubble-box form select, .search .bubble-box form .submit {
    display: none;
}

.search .bubble-box form {
  	position: relative;
    right: 200px;
    
}
}
