css:
@-moz-document url("https://www.schwans.com/secure/account/pastPurchases.aspx?viewAll=true") {
html
, body
, #wrapper
, #bodyContent
, #orderhistory-body
, .midpanelbot
{
	background: none !important;
  	background-color: #fff !important;
  	border: none !important;
}

#header
, .mboxDefault > *
, #global_promo
, #leftNav
, #rightcontent
, #footer
, #copyright
, #siteBreadcrumb
, #pastpurchases-tab
, #orderhistory-top
, .orderhistory-title
, #orderhistory-pager-top
, #orderhistory-filter
, #orderhistory-pager-bottom
, #pastpurchases-items .col3
, #pastpurchases-items .colLast
, .wrap_orderhistory > span
, .wrap_orderhistory > br
{
  display:  none;
}

#pastpurchases-items tr{
	
}
#pastpurchases-items tr:nth-child(4n+0){
	background-color: #eee;
}
#pastpurchases-items tr,
#pastpurchases-items td{
	page-break-inside: avoid;
}
#pastpurchases-items .divider{
	display: none
}
#pastpurchases-items .col1{
 	width: 48px;
 	padding: 4px 8px 4px 4px !important;
}
#pastpurchases-items .col1 img{
 	width: 100% !important;
 	height: auto !important;
}
#pastpurchases-items .col2{
 	padding: 0 8px 0 0 !important;
	width: auto !important;
	vertical-align: middle;
	padding-left: 1em;
	font-size: 8pt;
    text-align: right;
}
#pastpurchases-items .col2 p{
	font-size: 11pt;
    margin: 0;
  	padding: 0 1em 8px 0;
  	display: inline-block;
  	float: left;
}
#pastpurchases-items .col2 a{
  	color: #000;
  	text-decoration: none;
}
}
