css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.heise.de/),
               url-prefix(http://www.heise.de/newsticker),
               url-prefix(http://www.heise.de/extras)
{
  #bannerzone {
    display: none ! important;
  }
  #container {
    width:   99.9% ! important
  }
  #container_content, #sitemap {
    top:     0    ! important;
    width:   100% ! important;
  }
  #mitte {
    width:   100% ! important;   
  }
  #mitte #mitte_links {
    width:          77% ! important;
    background: #ffffff ! important;
  }
  #mitte #mitte_rechts {
    width:    23% ! important;
  }
  .adbottom {
    display: none ! important;
  }
}
@-moz-document url-prefix(http://www.heise.de/open),
               url-prefix(http://www.heise.de/ix),
               url-prefix(http://www.heise.de/foto),
               url-prefix(http://www.heise.de/netze),
               url-prefix(http://www.heise.de/mobil)
{
  #bannerzone {
    display: none ! important;
  }
  #container {
    width:   99.9% ! important
  }
  #container_content, #sitemap {
    top:     0    ! important;
    width:   100% ! important;
  }
  #mitte {
    width:   85% ! important;   
  }
}
