css:
/* i really want this to be global */
body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#leftMenu>div.mod-side-nav-recently-used,
body.os-win7.bg-body div#layBackground div.layout-head,
body.os-win7.bg-body div#pageContent div.col-main-sidebar,
div#idQbossHotbar{display:none!important}

body.os-win7.bg-body div.top-fix-wrap{width:90%!important}

body#QZ_Body#layBackground div#headContainer.lay_headContainer{height:50px!important}

body.os-win7.bg-body>div#layBackground>div.layout-nav
{height:0!important}
body.os-win7.bg-body div#layBackground div.layout-nav>div.layout-nav-inner
{height:150px!important;width:90%!important;padding:10px 0!important;position:fixed!important}
body.os-win7.bg-body div#layBackground div.layout-nav>div.layout-nav-inner>div.head-avatar
{left:72px!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent
{width:90%!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#leftMenu
{padding-top:140px!important;float:left!important;position:fixed!important;width:130px!important}

body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#main_feed_container
{width:inherit!important}
body.os-win7.bg-body div#layBackground>div.layout-background div#pageContent>div#main_feed_container>div.col-main-feed
{padding-left:50px!important;
width:inherit!important;}

body div#headContainer.lay_headContainer a#view_like_list
{border:0 rgba(0,0,0,0)!important;color:darkgray!important}

body.os-win7.bg-body>div.fix-layout{right:40px!important}
