css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");

/* Cor azul na aba selecionada */
#TabsToolbar .tabbrowser-tab[selected="true"] .tab-stack {
    /*background: rgba(0,100,255,0.2) !important;*/
    background: #4A90D9 !important;
    /*background: rgba(74, 144, 217, 0.9) !important;*/
    border-radius: 3px !important;
}

/* Margem no topo e em baixo das abas */
.tabbrowser-tab{
    margin-top: 6px !important;
    margin-bottom: 6px !important;
}

/* Texto branco na aba selecionada */
#TabsToolbar .tabbrowser-tab[selected="true"] .tab-text {
    color: #FFFFFF !important;
} 

/* Espaço entre as abas dos sites e padding entre a cor azul e o texto da aba*/
#TabsToolbar .tab-stack {
    margin: 0px !important;
    margin-left: 3px !important;
    margin-right: 3px !important;
    padding: 6px !important;
    border-image: none !important;
}

/* Espaço entre os botões da toolbar */
.toolbarbutton-1 {
    margin-left: 15px !important;   
}

/* Espaço entre as abas e o botão de recarregar */
.tabbrowser-tabs {
    margin-left: 7px !important;
}
#TabsToolbar {
    /*padding-left: 15px !important;*/
}

/* Espaço entre o botão de download, firefox e fechar */
#appmenu-toolbar-button, #cb-button {
    margin-left: 18px !important;
}

/* Margen entre o botão de fechar e a borda da janela */
#cb-button {
    /*border-left: 1px solid red !important;*/
    /*padding-left: 15px !important;*/
    margin-right: 13px !important;
    margin-left: 13px !important;
}

/* Separador entre o botão de Firefox e o botão de Fechar */
toolbarseparator {
    background: url("http://i.imgur.com/RDHnvhD.png") no-repeat scroll 0 9px rgba(0, 0, 0, 0);
    border: medium none !important;
    height: 5px !important;
    margin-left: 17px !important;
    margin-bottom: 7px !important;
    width: 1px !important;
}

/* Muda o botão do Firefox para a roda dentada */
#appmenu-toolbar-button {
    background: url("http://i.imgur.com/Zbt1diB.png") no-repeat scroll center center rgba(0, 0, 0, 0) !important;
    list-style-image: none !important;
    width: 17px !important;
}

/* Muda o ícone do botão de download */
#downloads-indicator-icon, #downloads-button {
    background: url("http://i.imgur.com/DEOAImD.png") no-repeat center center !important;
    list-style-image: none !important;
}

/* Moda o ícone do botão de Nova Aba */
#TabsToolbar > #new-tab-button, #TabsToolbar > toolbarpaletteitem > #new-tab-button, #navigator-toolbox[tabsontop="false"] #TabsToolbar > #new-tab-button, #navigator-toolbox[tabsontop="false"] #TabsToolbar > toolbarpaletteitem > #new-tab-button, #new-tab-button, #main-window #navigator-toolbox .tabs-newtab-button,
#main-window #navigator-toolbox #TabsToolbar #new-tab-button > .toolbarbutton-icon {
     list-style-image: none !important;
     background: url("http://i.imgur.com/xWqIg5O.png") no-repeat center center !important;  
}

/* Muda o ícone do botão de Fechar o Firefox */ 
.tabs-closebutton {
     background: url("http://i.imgur.com/henI4o2.png") no-repeat scroll center center rgba(0, 0, 0, 0) !important;
}

/* Retira a sombra do texto das abas. A sombra é resultado do tema White */
.chromeclass-toolbar-additional .toolbarbutton-text, .tabbrowser-tabs .tab-text, #TabsToolbar * { 
     color: black; 
     text-shadow: none; 
}
