        .vbmenu_option a:link {color: #000000 !important}
        .vbmenu_option {background: #d0d0d0 !important;}
        .vbmenu_hilite a:link {color: #000000 !important}
        .vbmenu_hilite {background: #d0d0d0 !important;}
        .vbmenu_popup a:link {color: #000000 !important}
        .vbmenu_popup {background: #d0d0d0 !important;}

/* Taustan ja solujen reunojen väritys */
   
        body {background: #dddeee !important;}
        td.alt1Active {background: #282828 !important;}
        .page {background: #e1e7e7 !important;}
        table {background: #e1e7e7 !important;}
        .alt1 {border: none !important;}
        .alt2 {border: none !important;}


.page a:link {color: #1515AA !important;}
.page a:visited {color: #5151e8 !important;}
.page a:hover, 

.bluetext {color: #1515AA !important;}

.button {color: #1515AA !important;}


/* Aihealue */
        td {font-size: 11pt !important;}
        td {color: #000000 !important;}
        td {padding: 2px !important;}
        .smallfont {font-size: 10pt !important;}

/* Viestikentän otsikkopalkki */
        .thead {font-size: 11pt !important;}
        .thead {color: #000000 !important;}
        .thead {background: #e1e1e1 !important;}
        .thead {padding: 2px !important;}
        .thead {border: none !important;}

/* Käyttäjänimikenttä */
	.bigusername {font-size: 11pt !important;}
