css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {
#onboarding-overlay,
#onboarding-overlay-button,
.edit-topsites-wrapper,
.section-title,
.prefs-pane-wrapper,
.top-sites-list .top-site-outer > div {
    display: none !important;
}
html,
body,
.body-wrapper,
main,
main section,
.top-sites-list {
    width: 100% !important;
    height: 100% !important;
    padding: 0 !important;
}
.outer-wrapper {
    padding: 6px !important;
}
main section {
    margin-bottom: 0 !important;
}
.top-sites-list .top-site-outer .default-icon {
    bottom: 30px !important;
    offset-inline-end: 0 !important;
}
.top-sites-list .top-site-outer .rich-icon {
    left: 0 !important;
    right: 0 !important;
    bottom: 30px !important;
    top: 0 !important;
    width: auto !important;
    height: auto !important;
    background-size: contain;
}
.top-sites-list .top-site-outer > a {
    position: relative !important;
    width: 100% !important;
    height: 100% !important;
}
.top-sites-list .top-site-outer {
    height: 16.666666% !important;
    width: 9.09% !important;
    position: static !important;
    display: block !important;
}
.top-sites-list .top-site-outer .tile {
    height: 100% !important;
    width: 100% !important;
    position: static !important;
}
.top-sites-list .top-site-outer .title {
    position: absolute !important;
    bottom: 0 !important;
    left: 0 !important;
    width: 100% !important;
    text-align: left !important;
    background-color: rgba(248, 249, 251, 0.8) !important;
    text-shadow: 0 0 2px rgba(248, 249, 251, 0.95) !important;
}
.top-sites-list,
.top-sites-list li {
    margin: 0 !important;
}
.top-sites-list {
    display: flex !important;
    flex-direction: row !important;
    flex-wrap: wrap !important;
}
.top-sites-list .top-site-outer:hover {
    z-index: 2 !important;
}
.top-sites-list .top-site-outer .title span {
    margin-left: 10px !important;
    margin-right: 10px !important;
}
}
