css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.zombiesamurai.pl") {

::-moz-selection {
    background: #999 !important;
}

*{
    background: #222 !important;
    text-align:justify;
    color: #777;
}

#content .regular h1 a {
    color: #999 !important;
}

#content ol li {
    color: #777 !important;
}


#content {
    padding: 20px 0px 0px !important;
    color: #777 !important;
}

#content blockquote p {
    color: #777 !important;
}

.frontpost {
    background: #222 !important;
    margin-top: 0px !important;
}

.frontpost-bg {
    background: #222 !important;
    /*padding: 140px 0px 0px !important;*/
}

.frontpost-inside {
    background: #222 !important;
    margin-top: 25px;
}

.side2-post {
    color: #777 !important;
}
    
.fbfooter, .topimg, .topimg-container, .topimg-place, .bottompodpis2, .leftside-over-over {
    display: none !important;
    height: 0px;
    width: 0px;
}

#content blockquote {
    border-left: 3px solid #A33 !important;
}
h1 {
    font-size: 32px !important;
}

h2,h3,h4,h5 {
    color: #A33 !important;
}

#content a {
    color: #A33 !important;
}

.side1 {
    border-left: 1px solid #777 !important;
    color: #777 !important;
}

.side1-link {
    color: #777 !important;
}

.sg #content h1 {
    color: #777 !important;
}
    
.ask-q {
    color: #777 !important;
}

}
