css:
@-moz-document url-prefix("https://www.cryptsy.com/markets") {

div#freshwidget-button
{
  display: none !important;
}

div#account-balances
{
  height: auto !important;
  overflow: visible !important;
  position: relative !important;
}

div.moduletable
{
  display: block;  
}

div.btc-markets-wrap,
div.ltc-markets-wrap,
div.xpm-markets-wrap
{
  top: 20px !important;
  position: relative !important;
  overflow: visible !important;
  height: auto !important;
}

div.moduletable.moduletable-account-balances,
div.moduletable.moduletable-usd-markets,
div.moduletable.moduletable-btc-markets,
div.moduletable.moduletable-ltc-markets,
div.moduletable.moduletable-xpm-markets
{
  height: auto !important;
}

div.moduletable.moduletable-cryptsy-points,
div.moduletable.moduletable-usd-markets,
div.iScrollVerticalScrollbar 
{
  display: none;
}

.separator 
{
  position: relative !important;
}
}
