css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Hide the additional gray bar on Android.StackExchange with "floating" MultiColloder™ */
@-moz-document domain("android.stackexchange.com") {
  body { background: none !important;}
}

@-moz-document url-prefix("http://stackoverflow.com"),
               url-prefix("http://meta.stackoverflow.com"),
               domain("askubuntu.com"),
               domain("serverfault.com"),
               domain("stackapps.com"),
               domain("superuser.com"),
               domain("stackexchange.com") {

  /* Hide gravatars. For the MultiCollider™ ".avatar-me" should be sufficient; including the others affects chat as well */
  /*
  .user-gravatar32,
  .gravatar,
  .avatar-me {
    display: none !important;
  }
  */
  /* Alternatively: Replace avatar with your username */
  /*
  a.profile-me > div > img {
    visibility:hidden !important;
    vertical-align: middle !important;
  }
  a.profile-me > div:after {
    content:attr(title);
    text-decoration:none !important;
    color:rgb(130, 130, 130);
  }
  */
  /* end avatar */
  .topbar {
    color: #999 !important;
    background: #eee none !important;
    /* Make the topbar always float on top, even when scrolling */
    position: fixed; left:0; top: 0; z-index:555;
  }
  #header { margin-top:30px !important; }
  .topbar .topbar-icon:hover,
  .topbar .topbar-icon-on,
  .topbar .topbar-icon-on:hover,
  .topbar .topbar-links .profile-me:hover,
  .topbar .topbar-links .topbar-menu-links a:hover {
    background-color: #ddd !important;
  }
  /* Hide achievements icon when there are no news */
  /*
  .topbar .icon-achievements,
  .topbar .icon-achievements .unread-count {
    background: inherit !important;
    color: #999 !important;
  }
  */
  .topbar .topbar-links .topbar-menu-links a,
  .topbar .topbar-links .topbar-menu-links a:visited,
  .topbar .topbar-links .topbar-flair .reputation,
  .topbar .topbar-links .topbar-flair .badgecount {
    color: #999 !important;
  }
  .topbar .topbar-links .search-container input[type="text"] {
    border-color: inherit !important;
  }
  .topbar .icon-site-switcher {
    background-position: 11px 11px !important;
    /* SE logo gray */
    background-image: url(data:image/png;base64,) !important;
    /* SE logo color */
    /* background-image: url(data:image/png;base64,) !important; */
  }
  /* make the inbox & achievements dropdowns wider */
  .topbar-dialog { width:420px !important; }
  .topbar-dialog.help-dialog { width:300px !important; }
  .inbox-item .item-content { width:358px !important; font-size:11px !important; }
  .achievements-dialog .message-text { width:323px !important; font-size:11px !important; }
  /* SearchBox modifications. First block just makes it a little more decent. */
  .topbar .topbar-links .search-container input[type="text"] {
     border:1px solid rgba(0,0,0,.2) !important;
     margin-top:5px !important;
     border-radius:6px !important;
     font-size:11px !important; height:13px !important;
  }
  /* Enable the two blocks below if you want an auto-expanding search box. */
  /*
  .topbar .topbar-links .search-container input[type="text"] {
      width:50px !important;
      opacity:.7 !important;
      background-color:#fff !important;
      background-image:none !important;
      padding-left:5px !important;
      transition:width .2s;
  }
  .topbar .topbar-links .search-container input[type="text"]:focus {
      width:188px !important;
      opacity:1 !important;
  }
  */
}

@-moz-document domain("askubuntu.com") {
  .topbar {
    background:#feb none !important;
    /* Make the topbar always float on top, even when scrolling */
    position: relative; left:0; top: 0; z-index:0;
  }
  #header { margin-top:0 !important; }
}
