css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

/* blue fix */
/*div.search_form_row {background-color: rgba(234, 228, 204, 0.6)}
div.l-header .selection_nipple {border-bottom-color: rgba(234, 228, 204, 0.6)}*/

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.post.new_post {
	background-color: rgba(255,255,255,.4) !important;
}
.post.new_post:hover {
	background-color: rgba(255,255,255,1) !important;
}
.post.new_post > .post_avatar:after {
	border-right-color: rgba(255,255,255,.4) !important;
}
.post.new_post:hover > .post_avatar:after {
	border-right-color: rgba(255,255,255,1) !important;
}
.tab_bar:hover > .selected > .selection_nipple:not(.white) {
	border-bottom-color: rgba(255,255,255,1) !important;
}

.selection_nipple:not([class="selection_nipple white"]),
.post.new_post,
.post.new_post > .post_avatar:after {
	transition: .3s;
}

.new_post_label > .new_post_label_text,
.new_post_label > I {color: #b4e8f5 !important}

body {
	background: url('http://img15.hostingpics.net/pics/968621KNBrender.png') bottom left #b4e8f5 no-repeat fixed !important;
}

/*div.l-content*/#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background-color: transparent!important;
}

* {
	text-shadow: none !important;
	color: #676767 !important;
}

ol#posts li.notification {
	background-color: #ceecf4 !important;
	border-bottom: 2px solid #9d92a5 !important;
	/*color: #ceecf4 !important;*/
}

#right_column a#dashboard_switch_blog_menu_current:hover {
	background: #ceecf4;
}

#nav .nav_item.active {
	background-color: #ceecf4;
}

#left_column #posts .post .permalink {
	background-color: #ceecf4;
}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current {
	background-image: none !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #ceecf4 !important;
}

.notification_type_icon {
	display: none !important;
}

.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes {
	color: #A099A1 !important;
	background: #92bdc8 !important;
}

a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor {
	background-color: #92bdc8 !important;
	background-image: none !important;
	border: 0px !important;
}

div.gradient,
div.dashboard_nav_item, 
a.permalink {
	background-color: #92bdc8 !important;
	opacity: .9;
	background-image: none !important;
	border: 0px !important;
}

.content.is_media, 
.mask.top, 
.mask.bottom {
	background: none !important;
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	border-radius: 16px !important;
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
	-moz-box-shadow: 1px 1px 3px #000000 !important;
}

#auto_pagination_loader {
	background: #A099A1 !important;
}

div.return_to_top_icon {
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important;
}

#left_column #posts .notification {
	background-color: #ceecf4 !important;
	color: #666 !important;
	border-bottom-color: #eee !important;
}

#new_post_notice_container {
	color: #fff !important;
}

#new_post_notice_container a {
	color: #fff !important;
}

.permalink {
	background: none !important;
	width: 16px !important;
	height: 16px !important;
	background: #ceecf4 !important;
	border-bottom-left-radius: 10px !important;
	border-top-right-radius: 10px !important;
	margin-top: 3px !important;
	margin-right: 4px !important;
}
}
