css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("computerbase.de") {

* {background-color: transparent !important; color: #BBB !important; border-color: #333 !important; text-shadow: none !important}
html, #page-wrapper {background: #151515 !important}
.toc-page-number  {color: #999 !important}
h1, h2, h3, h4, h5, b, .list-heading, .group-caption, .article-title, .article-view__title, .spoiler-title, .chart-title, .product-title, .tabs-li .active, .tabs-li .active * {color: #DDD !important}
div.date.date-highlight {color: #F70 !important}
.product-title {text-shadow: 0 1px #000 !important}
.bar span {color: #FFF !important; text-shadow: 0 0 2px #000 !important}
a, a *, .dropdown-link:visited *, .tabs-li a:visited, .tabs-li a:visited * {color: #009EE7 !important}
a.wikipedia, a.wikipedia:visited {border-color: #009EE7 !important}
a:visited, a:visited * {color: #6AACE2 !important}
a:hover, a:hover * {color: #FFF !important; text-shadow: 0 0 5px rgba(255,255,200,0.9) !important}

.header, .image-overlay {background: rgba(0,0,0,0.9) !important}
.subheader {background: #18355f !important}
.subheader::before {background: #0A0A0A !important}
.mainmenu-panel, .menu__panel {background: #222 !important}

.footer1, .footer2 {background: rgba(9,9,9,0.9) !important}

.block--top, .block1--top, .footer .block1 > .wrapper {background: transparent !important}

.prev-link.disabled, .next-link.disabled {opacity: .2 !important}

:focus {outline: none !important}

#desktop-browser-notice {background: #100 !important; border-color: #200 !important}

#mobile-nav {background: linear-gradient(#111, #333) !important; border-color: #444 !important}
#mobile-nav a {color: #FFF !important; text-shadow: 0 1px #000 !important; border-color: #222 !important}
#mobile-nav a:hover {background: linear-gradient(#272727, #333) !important}

ul.content-list li, ol.content-list li {background: none !important}
#content-header, ul.like-bar {background: transparent !important; border-top: none !important; border-bottom: 1px solid #222 !important; box-shadow: 0 1px #000 !important}

#pre-footer {background: #000 !important; border-color: #272727 !important}
#cb-footer {background: linear-gradient(#272727, #000) !important}

div.date, .update-link, .taglist span, .taglist a {background: linear-gradient(#111, #333) !important; border: 1px solid #222 !important; border-radius: 2px !important; text-shadow: 0 1px #000 !important}
.dropdown-link, .tabs-li a, .tabs-li .active, .tabs-li .disabled {background: #191919 !important; border-radius: 2px !important}

.spoiler-icon {background: linear-gradient(#222, #333) !important; color: #CCC !important; border-color: #444 !important}
.arrow-down:after {border-top-color: #777 !important}

.gallery-nav div {background: rgba(0,0,0,.85) !important; color: #FFF !important}

.toc-page-number {border-color: #333 !important}
.toc-page-number.current {background: #333 !important; color: #FFF !important}
.toc a:hover {
  background: transparent !important;
  box-shadow: inset 0 0 3px rgba(0,0,0,0.6) !important;
  text-shadow: 0 0 3px rgba(255,255,255,0.5) !important}
.toc a:hover .toc-page-number {background: #333 !important}

table.tabular {background: #1D1D1D !important; border-color: #333 !important}
#download_urls th, table.tabular tr th, #content_table_hdr .btn3, th.lh1, .product-title, #content .table th {color: #FFF !important; text-shadow: 0 1px #000 !important; background: linear-gradient(#222, #333) !important}
table.tabular tr.odd td, table.tabular tr:nth-child(2n) td, #content .table tr:nth-child(2n), #download_urls tr.row_even {background: #252525 !important}
.product-box {background-color: #151515 !important; box-shadow: inset 0 0 4px #000}
.product-header {background: linear-gradient(#222, #333) !important}


#page-home, .quote_container {background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAEAQMAAACTPww9AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAGUExURRERESYmJvXb2rIAAAAQSURBVAgdYxBgUGBwYGgAAAJYAPGe8/X/AAAAAElFTkSuQmCC) !important}
.quote_container {border-color: #444 !important}
.ui-li {text-shadow: 0 2px #000 !important}
h1.pagetitle {text-shadow: 0 2px #000 !important}
.ui-btn, ol#posts {background: #151515 !important}
.ui-btn-inner {border-color: transparent !important}
.ui-bar-b, .ui-bar-c, .ui-bar-d, .ui-body-c {background: #222 !important; border-color: #222 !important; border-top-color: #333 !important; box-shadow: 0 0 6px #090909 !important;}
.pagenav a {background: transparent !important; border: none !important}
.ui-vbpagenav-pagenumber.ui-btn-up-e {border-bottom: 3px solid #009EE7 !important}

.ui-li-count {background: linear-gradient(#272727, #333) !important; border-color: #444 !important; text-shadow: 0 1px #000 !important}
span.ui-btn-corner-all.ui-shadow, .ui-vbpagenav-first .ui-icon, .ui-vbpagenav-last .ui-icon {background-color: #009EE7 !important; border-color: #111 !important}
span.ui-btn-corner-all.ui-shadow .ui-icon-arrow-r {background-color: #037 !important}
.ui-icon-shadow {box-shadow: 0 1px #000 !important}

.postuseravatarlink img {border-radius: 100% !important; box-shadow: 0 0 4px #000 !important; min-height: 44px !important; max-height: 44px !important; min-width: 44px !important; max-width: 44px !important}


textarea, select, input {
  background: rgba(35,35,35,.7) !important;
  border: 2px solid rgba(200,200,200,.1) !important;
  box-shadow: inset 0 0 3px #000 !important;
  transition: 0.4s !important}
select:focus button {background-position: center 5px !important}
textarea:hover, select:hover, input[type="text"]:hover, input[type="search"]:focus, input[type="password"]:hover, input[type="number"]:hover {border-color: rgba(200,200,200,.2) !important}
textarea:focus, select:focus, input[type="text"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="number"]:focus {border-color: rgba(200,200,200,.0) !important; box-shadow: inset 0 0 3px #000, 0 0 10px #009EE7 !important}


.double-arrow:before, .double-arrow:after {border-left-color: #009EE7 !important}
.double-arrow:hover:before, .double-arrow:hover:after {border-left-color: #FFF !important}
.toc-title-1 {background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAaSURBVBhXYwCBmTNn/gdSIIwKiJcgIMDAAADS8xLukv7UxgAAAABJRU5ErkJggg==) no-repeat scroll 14px 8px !important}

img[src="images/misc/unknown.gif"] {background: url(data:image/png;base64,) !important; min-width: 0 !important; max-width: 0 !important; padding-left: 44px !important}
.icon-mail {background-image: url(data:image/svg+xml;base64,) !important}
.article-prev-link, .article-next-link {background-image: url(http://abload.de/img/pagelinks-arrow223zxh.png) !important}
.chart_groups td.value, .chart_groups td.group_footer {background-image: url(http://www.abload.de/img/grid_new1saqm.png) !important}
.bar_center {background-image: url(http://abload.de/img/bars2bxuo6.png) !important}
.bar_left, .bar_right {background-image: url(http://abload.de/img/edges207ue6.png) !important}

.chart {background: #000 !important; border: 2px solid #111 !important; box-shadow: 0 0 6px #000 !important}
.chart-bar {border-radius: 3px !important; box-shadow: 0 0 3px #000, inset 0 0 2px #111 !important}
.chart-bar-c1 {background: #2c5b9c !important}
.chart-bar-c2 {background: #f0a831 !important}
.chart-bar-c3 {background: #c51429 !important}
.chart-bar-c4 {background: #6eac16 !important}
.chart-bar-c5 {background: #95adcd !important}
.chart-bar-c6 {background: #f7d398 !important}
.chart-bar-c7 {background: #e28994 !important}
.chart-bar-c8 {background: #b6d58a !important}
.chart-bar-c9 {background: #333 !important}
.chart-bar-c10 {background: #111 !important}
.chart-bar-c11 {background: #666 !important}
.chart-bar-c12 {background: #999 !important}
.chart-bar-c13 {background: #ccc !important}
.chart-label, .chart-item strong, .chart-row:hover .chart-item {color: #FFF !important; text-shadow: 0 0 3px #000 !important}
.chart-comment em {background-color: #666 !important}
.chart-row:hover .chart-bar-wrapper {background: transparent !important;}
.chart-row:hover .chart-bar {box-shadow: 0 0 3px #000, inset 0 0 2px #FFF !important}

.svg-chart {
  filter: url(data:image/svg+xml;base64,#1) !important;
  -webkit-filter: invert(100%) hue-rotate(180deg) !important;
  transform: translate3d(0,0,0) !important;
  box-shadow: 0 0 6px #FFF !important;
  border-radius: 8px !important}
}
