css:
@-moz-document domain(fimfiction.net), regexp("!('http://www.fimfiction.net/manage_user/notifications')")
{

#title, #twitter-widget-0{
    display:none;
}
div.fb-like, div.fb-like-box, div.g-plus/*, div.featured_box*/{
    display:none !important;
}
body{
    font-size:16px !important;
  	background-color: #000000;
    background-image:none;
    /*letter-spacing:-0.1px !important;*/
    /*line-height:0.5em !important;*/
}
a.styled_button, input.styled_button{
    padding: 4px !important;
}
blockquote{
    padding:2px !important;
}
 
/*NAV BAR*/
div.nav_bar{
    height:36px !important;
}
div.nav_bar input.search_submit{
    padding: 6px !important;
}
div.nav_bar div.light a.button{
    height:36px !important;
}
div.nav_bar div.light div.textbox{
    padding:0px !important;
}
 
/*USER TOOLBAR*/
div.user_toolbar .button{
    line-height:35px !important;
    padding: 0px 6px !important;
    /*font-size:0.7em !important;*/
}
 
/*USER BAR (above userpages etc)*/
div.user_bar{
    margin-bottom:2px !important;
}
 
/*FRONTPAGE*/
div.content_box h1{
    line-height:1.5em !important;
}
img.avatar, img.story_thumbnail{
    padding:0px !important;
    margin:2px !important;
}
div.content_box li{
    line-height:1em !important;
    padding:1px !important;
}
ul.list li{
    min-height:55px;
}
ul.story_list li{
    min-height:0px;
}
a.story_link{
    font-size:1em !important;
    font-weight:bold !important;
}
span.info{
    font-size:0.75em !important;
    letter-spacing:-1px !important;
}
*{
    transition:none !important;
}
 
/*ANNOYING SPACE AT BOTTOM OF FRONTPAGE*/
div.ad_container{
    display:none !important;
}
 
/*FOOTER*/
div.footer{
    padding:0px !important;
    font-size:0.8em !important;
}
div.footer div.block{
    line-height:1.6em !important;
}
div.footer div.block h1{
    padding-bottom:3px !important;
    margin-bottom:4px !important;
}
 
/*CONTENT*/
div.content{
    padding-top:0px !important;
}
/*content box*/
div.content div.content_box{
    /*margin: 6px 0px !important;*//*LEAVE THIS COMMENTED BECAUSE IT SCREWS UP EDIT ACCOUNT OTHERWISE*/
}
div.content div.content_box hr{
    margin: 1px 0px !important;
}
div.content div.post_content_box{
    margin-left:64px !important;
}
/*header*/
div.content_box div.content_box_header{
    padding-left: 10px !important;
}
div.content_box_header h2{
    line-height: 40px !important;
}
/*div.content_box_header h2 input{
    padding:4px !important;
}*/
div.content_box_header h2 a{
    padding-bottom:1px !important;
}
 
/*MAIN*/
/*add comment*/
div.emoticons_panel{
    height:200px !important;
}
div.emoticons_panel div.inner_padding{
    margin:1px !important;
}
div.add_comment_toolbar{
    padding:2px !important;
}
div.add_comment div.textbox_container textarea{
    /*height:200px !important;*/
    line-height: 1.1em !important;
}
 
/*COMMENT (that is, the list with already made comments)*/
div.comment{
    padding:2px !important;
    line-height:1em !important;
}
div.comment div.author{
    display:inline !important;
    width:120px !important;
    font-size:0.8em !important;
}
div.comment div.author div.name{
    padding:2px !important;
    text-align:center !important;
}
div.comment div.author img.avatar{
    margin-top:2px !important;
}
div.comment_arrow{
    display:none;
}
div.comment div.data{
    display:block !important;
    /*margin-left:140px !important;*/
    padding:5px !important;
    line-height:1em !important;
}
/*div.comment div.data img[style="height:27px;"]{
    /*make the default emoticons smaller*//*
    height:16px !important;
}*/
div.comment div.data div.comment_information{
    padding-bottom:1px !important;
    margin-bottom:4px !important;
    word-spacing:-2px !important;
    /*letter-spacing:-0.5px !important;*/
}
div.comment div.data div.comment_information div{
    margin-top:0px !important;
}
div.comment div.data div.comment_information span{
    margin:0px !important;
}
 
/*USERPAGE SHIZL*/
div.user_page_content_box p{
    line-height:1.1em !important;
    /*margin:6px 0px !important;*/
}
div.user_page_content_box{
    margin:6px 0px !important;
}
div.user_page_content_box h2, div.comment_content_box h2, div.blog_post_content_box h2{
    line-height:1em !important;
}
 
 
/*DARK/LIGHT TOOLBAR*/
div.light_toolbar,
div.dark_toolbar{
    padding: 12px !important;
}
div.light_toolbar ul a,
div.dark_toolbar ul a{
    margin:2px 0px 2px -1px !important;
    padding:4px !important;
}
/*div.chapter [toolbar_padder]{
 	height:10px !important; 
}*/
 
/*SIDEBAR*/
div.list_element_contents a{
    font-size:1.1em !important;
}
 
/*BLOGS*/
div.blog_post_content_box{
    font-size:0.9em !important;
}
div.blog_post_content_box h2 a{
    line-height:1em !important;
    font-size:1em !important;
}
div.blog_post_content p{
    line-height:1.1em !important;
}
 
/*STORY LISTINGS*/
img.story_avatar{
    max-width:64px !important;
    width:64px !important;
    margin-left:70px !important;
}
div.story_content_box{
    margin-bottom:10px !important;
    font-size:0.9em !important;
}
div.story_content_box .content_box_header div{
    margin:0px !important;
    padding:-10px !important;
}
div.story_content_box .content_box_header .right{
    margin-right:0px !important;
    margin-top:0px !important;
    /*max-height:40px !important;*/
}
div.story_content_box .content_box_header .title{
 	height:60px !important;
}
a.read_it_later_widget{
    /*display:none !important;*/
    margin:-13px 0 -13px -5px !important;
    /*padding:0px !important;*/
}
div.story_content_box .content_box_header h2{
    margin-left:0px !important;
}
div.story_content_box .content_box_header h2 a.story_name{
    font-size:0.9em !important;
}
div.story_content_box .padding{
    padding:2px !important;
}
div.story_content_box .description a.story_category,
div.story_content_box .description a.story_category:hover{
    padding:3px 4px !important;
}
div.story_content_box .description p{
    line-height:1.1em !important;
    letter-spacing:-0.1px !important;
}
div.story_content_box img.story_image{
    padding:0px !important;
    margin:0 3px 0 3px !important;
    /*max-width:150px;*/
}
/*div.story_content_box .extra_story_data div,
div.story_content_box .extra_story_data span{
    padding:1px !important;
    line-height:1em !important;
    display:inline-block !important;
}*/
 
/*DOWNLOAD LINKS (txt/html/epub)*/
div.download_container div{
    /*line-height:1em !important;*/
    display:inline-block !important;
}
div.download_container img.place_holder{
    display:none !important;
}
div.download_container:HOVER div{
    display:inline-block !important;
    position:relative !important;
    left:0px !important;
    top:0px !important;
    z-index:0 !important;
    border:0px !important;
    border-radius:0px !important;
    background:rgba(255,255,255,0) !important;
    padding:0px !important;
    line-height:1em !important;
}
div.download_container div img{
    margin:-2px 1px !important;
}
div.download_container div a:hover{
    background:rgba(255,255,255,0) !important;
}
div.download_container div a{
    display:inline !important;
    border-radius:0px !important;
}
 
ul.chapters{
    margin:0px !important;
}
/*PILL BUTTONS (the character icons?)*/
.pill_button, .styled_button{
    margin:0px 2px !important;
}
.pill_button img{
    padding:0px !important;
    max-height:25px !important;
}
 
/*PROPERTIES (=click the Filter button and also New Story and also Edit Account*/
table.properties td{
    padding:4px !important;
    line-height:1.2em !important;
}
table.properties div.characters{
    padding:4px !important;
    margin-top:4px !important;
}
table.properties div.characters label{
    padding:1px !important;
    margin:1px !important;
}
table.properties input,
table.properties select,
table.properties textarea{
    padding:3px !important;
}
table.properties input[type="text"]{
    padding-left:24px !important;
    background-position:4px center !important;
}
table.properties textarea{
    line-height:1.3em !important;
}
table.properties input.radio,
table.properties input.checkbox,
table.properties input[type="checkbox"] {
    margin:2px !important;
}
table.properties label,
table.properties div.rounded_container{
    padding:2px !important;
    margin:2px !important;
}
table.properties div.external_account{
    padding:0px !important;
    margin-bottom:5px !important;
}
table.properties label.category{
    padding:1px 4px !important;
}
 
/*SEARCH AND SHIT/FILTERING ETC*/
div.search_options .characters_dropdown div{
    margin:0px !important;
    padding:0px 5px !important;
}
div.search_options .characters_dropdown div img{
    max-height:20px;
    max-width:20px;
    margin:0px !important;
    padding:0px !important;
    /*height:20px !important;*/
}
div.search_options .label{
    margin:0px !important;
    padding:2px !important;
}
div.search_options #characters_search_container{
    min-height:20px !important;
}
 
/*PAGINATION HELL*/
div[style="text-align:left; margin:15px 0px;"]{
    margin:2px 0px !important;
    padding:0px !important;
}
 
/*NOTIFICATIONS (and tabs as well)*/
div.tabs ul li a{
    margin:2px !important;
    padding:0px !important;
}
 
 
/*FEED SO IT IS USABLE AGAIN*/
div.feed#feed div.feed_item.feed_group_item div.compressed_view,
div.feed#feed div.feed_item.feed_new_chapter div.compressed_view{
    display:none !important;
}
div.feed#feed div.feed_item.feed_group_item div.feed_body{
    display:block !important;
}
 
div.feed_item{
    margin:8px 0 !important;
}
div.feed_item div.feed_header h1{
    margin:0 !important;
    padding:0 !important;
    line-height:1.2em !important;
}
div.feed_item div.arrow{
    display:none !important;
}
div.feed_item h1 img{
    display:none !important;
}
div.feed#feed div.feed_item div.compressed_view{
    margin:0 !important;
    padding:2px !important;
    line-height:1em !important;
}
div.feed#feed div.feed_item.feed_group_item div.feed_body{
    margin:0 !important;
    padding:2px 0 0 0 !important;
}
div.feed#feed div.feed_item.feed_group_item ul.group_stories{
    margin:0 !important;
    padding:0 0 0 20px !important;
    line-height:1em !important;
}
div.feed#feed div.feed_item.feed_group_item ul.group_stories li a.group_folder{
    padding:2px 2px !important;
    margin:0 !important;
    background:none !important;
    border:none !important;
}
div.feed#feed div.feed_item.feed_group_item ul.group_stories li a.group_folder:hover{
    text-decoration:underline !important;
}
 
 
/******DASHBOARD HELL*/
div.thread_list>table>tbody>tr>td>img.avatar{
    width:32px !important;
    height:32px!important;
}
div.thread_list>table>tbody>tr>td{
    padding:1px 0 !important;
    line-height:1.2em !important;
}
 
 
 
/***********ALWAYS SHOW EXTRA CHAPTERS******/
ul.chapters div.chapter_container.chapter_expander{
    display:none;
}
ul.chapters div.chapter_container.chapter_container_hidden{
    display:block !important;
}
 
/***********ALWAYS SHOW ALL GROUPS A STORY IS IN****/
ul#story_group_list li.hidden{
    /*display:block !important;*/
}
/*ul#story_group_list {
    display:none !important;
}*/
 
/***compact date/time labels when added  TODO:display raw date with JS***/
div.before{
    display:none !important;
}
 
ul#story_group_list li{
    padding:0 !important;
    margin:0 !important;
    min-height:25px !important;
}
ul.list li div.list_element_contents{
    padding: 2px !important;
}
ul.list li div.list_element_contents div.date{
    padding:0px 1px !important;
    margin:0px !important;
}
}
