css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') {
.svg-logo, .logo .png-logo, .logo:hover .opacity-switch .preload-container
	{
	background: url('http://i.imgur.com/B770IQO.png*[[logo1]]*//*[[logo2]]*/ /*[[logo3]]*/')!important;
	background-size: contain !important;
	background-repeat: no-repeat !important;
	width: 206px !important;
	height: 55px;
	/*	position: absolute; top: 5px;*/
	
	padding: 0px 0;
	margin-right: 3px;
	left: 13px;
	}

.logo:hover .opacity-switch .png-logo {
    opacity: 1;
	}

.opacity-switch .preload-container {
    display: none;
	}
  
  .tab_bar {
    right: 5px!important;
    }

.popover_menu_item .thumb { 
    right: 33px !important;
	}

.search_popover * {
  box-sizing: border-box;
  width: 188px;
}

.logo .large-shadow, 
.logo .small-shadow, 
.logo .outer, 
.logo .inner {
	display:none!important;}
  
}
