css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* Autohides scrollbar */
scrollbar[orient="vertical"]:not(:hover),
scrollbar[orient="horizontal"]:not(:hover) {
    opacity: /*[[ah]]*/ !important;
}


/*[[arrow]]*/
/* Removes whitespace and allows scrollbar to appear */
 scrollbar {
    position: relative !important;
    -moz-appearance: none !important;
    background: none !important;
    z-index: 999999 !important;
    transition: opacity 1s ease !important;
}


/* Appearance of scrollbar thumb */
 scrollbar thumb {
    position: absolute !important;
    -moz-appearance: none !important;
    background: rgba(/*[[color]]*/.6) !important;
    border-radius: /*[[radius]]*/ !important;
    /*[[fade]]*/
    /*[[border]]*/
    /*[[glowshad]]*/
}


/* Color of scrollbar thumb on mouseover */
 scrollbar thumb:hover {
    background: rgba(/*[[color]]*/.8) !important;
}


/* Color of scrollbar when clicked */
 scrollbar thumb:active {
    background: rgba(/*[[color]]*/1) !important;
}


/* Scrollbar sizes */
 scrollbar[orient="vertical"] {
    margin-left: -/*[[margin]]*/px !important;
}
 scrollbar[orient="horizontal"] {
    margin-top: -/*[[margin]]*/px !important;
}


/*Scrollbar padding*/
 scrollbar[orient="vertical"] {
    padding: 0px /*[[padding]]*/px 0px 0px !important;
}

 scrollbar[orient="horizontal"] {
    padding: 0px 0px /*[[padding]]*/px 0px !important;
}

style_setting_options.value:
/*Appearance of scrollbar buttons, also influences scrollbar size*/
scrollbar scrollbarbutton {
  -moz-appearance: none !important;
  opacity: .5 !important;  
  background: none !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  background-size: cover !important;
  border-style: none !important;
  min-width: calc(/*[[margin]]*/px - /*[[padding]]*/px) !important;
  max-width: calc(/*[[margin]]*/px - /*[[padding]]*/px) !important;
  min-height: calc(/*[[margin]]*/px - /*[[padding]]*/px) !important;
  max-height: calc(/*[[margin]]*/px - /*[[padding]]*/px) !important;
}


/*Changes color of scrollbar button when hovered over*/
scrollbar scrollbarbutton:hover {
  opacity: 1 !important;
}


/*Fixes rotation on arrows*/
scrollbar[orient="vertical"] scrollbarbutton {
  transform: rotate(90deg) !important;
}


/*Scrollbar button backgrounds*/
  scrollbar scrollbarbutton[type="decrement"] {
  background-image: url("data:image/png;base64,") !important;
}

  scrollbar scrollbarbutton[type="increment"] {
  background-image: url("data:image/png;base64,") !important;
}

/* Hides scrollbar buttons */
scrollbar scrollbarbutton {
    visibility: collapse !important;
}
