css:
@-moz-document url-prefix("http://myanimelist.net/animelist"),
               url-prefix("http://myanimelist.net/mangalist"),
               url-prefix("https://myanimelist.net/animelist"),
               url-prefix("https://myanimelist.net/mangalist")
               
               /*  
               
                                              By
               
                                sKonabu / Konabu 
               
                                     inspired by
                         crayon by sunnysummerday 
               
               
               */
{

	body, html {
		color: #414141 !important;
		font-size: 13px !important;
		text-transform: lowercase !important;
		margin: 0px !important;
		background: url(http://colourlovers.com.s3.amazonaws.com/images/patterns/3518/3518772.png) repeat #eaeaea !important;
	}
	 

	#list_surround {
		margin: 0 auto !important;
		width: 950px !important;
	}
	#list_surround table {
		width: 100% !important;
		margin: 0px !important;
	}
	 

	a {
		color: #414141 !important;
		text-decoration: none !important;
	}
	a:visited {
		color: #333333 !important;
		text-decoration: inherit !important;
	}
	a:hover {
		color: #6bbfe3 !important;
		text-decoration: none !important;
		font-style: normal !important;
		font-weight: normal !important;
	}
	 

	.td1 {
		background-color: #fcfcfc !important;
		border-color: #ebebeb !important;
		border-style: solid !important;
		border-width: 0px !important;
		font-style: normal !important;
		color: #414141 !important;
		padding: 3px !important;
	}
	 

	.td2 {
		background-color: #ffffff !important;
		border-color: #ebebeb !important;
		border-style: solid !important;
		border-width: 0 !important;
		color: #414141 !important;
		font-style: normal !important;
		padding: 3px !important;
	}
	/* Border for the "#" column.
	*/
	.td1:first-child,
	.td2:first-child,
	.table_header:first-child {
		border-right: none !important;
	}
	.td1:first-child,
	.td2:first-child,
	.table_header:first-child {
		border-left: 1px solid #ebebeb !important;
	}
	.td1:last-child,
	.td2:last-child,
	.table_header:first-child {
		border-right: 1px solid #ebebeb !important;
	}
	 

	.table_header {
    background: #fcfcfc !important;
    color: #414141 !important;
    margin: 5px !important;
    padding: 4px 4px !important;
    font-family: Segoe UI !important;
    font-size: 10px !important;
    border-width: 0px !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5);
    border-bottom: 2px solid #6bc8ae !important;
    transition: 0s; }
	 
	.td1 div[style^='float'],
	.td2 div[style^='float'] {
		margin-left: 7px !important;
		/*padding-left: 1px;
		border-left: 1px solid #C4C4C4;*/
	}
	 

	.table_headerLink {
		color: #414141 !important;
		font-weight: normal !important;
	}
	.table_headerLink:visited {
		color: inherit !important;
	}
	.table_headerLink:hover {
		color: #6bbfe3 !important;
	}
	 
     
	td.table_header {
	    padding: 4px !important;
	    color: #aeaeae !important;
	    font-weight: normal !important;
	    font-style: normal !important;
		border-top-color: #ebebeb !important;
		border-left-color: #ebebeb !important;
		border-bottom-color: #6bc8ae !important;
		border-right-color: #ebebeb !important;
	}

	 

	.form {
		border-color: #ebebeb !important;
		border-style: solid !important;
		border-width: 1px !important;
		color: #414141 !important;
		font-family: Segoe UI !important;
		font-size: 12px !important;
		padding: 2px !important;
	}
	 

	.status_selected {
		background-color: #e8608c !important;
		border-color: #ebebeb !important;
		border: none !important;
		padding: 1px 2px !important;
		color: #fcfcfc !important;
		padding: 5px !important;
		font-style: normal !important;
		font-weight: bold !important;
		width: 16.7% !important;
	}
	.status_selected a {
		color: inherit !important;
	}
	.status_not_selected {
		background-color: #fcfcfc !important;
		border-color: #dddddd !important;
		border-style: solid !important;
		border-bottom: 2px solid #6bc8ae !important;
		border-width: 0 0 1px !important;
		padding: 2px 3px !important;
		color: #aeaeae !important;
		font-size: 16px !important;
		padding: 5px !important;
		width: 16.7% !important;
	}

	.header_cw {
	}
	.header_completed {
	}
	.header_onhold {
	}
	.header_dropped {
	}
	.header_ptw {
	}
	.header_title {
    background: #fcfcfc !important;
    color: #414141 !important;
    text-align: center !important;
    border-radius: 1px !important;
    margin: 5px !important;
    padding: 4px 5px !important;
    font-family: Segoe UI !important;
    font-size: 18px !important;
    font-weight: normal !important;
    border-color: #dddddd !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5);
    border-bottom: 1px solid #6bc8ae !important;
 
	}
	.category_totals {
	text-align: center !important;
    background: #fefefe !important;
    color: #414141 !important;
    border-radius: 1px;
    margin: 6px !important;
    padding: 6px 8px !important;
    font-family: Segoe UI !important;
    border-color: #ebebeb !important;
    box-shadow: 0 0 2px rgba(65,65,65,0.5);
    border-bottom: 2px solid #e8608c !important;
    }
	
	#grand_totals {
	    background: #fcfcfc !important;
	    padding: 4px 5px !important;
	    font-weight: normal !important;
	    color: #414141 !important;
	    border-bottom: 2px solid #6bbfe3 !important;
		margin: 15px 0 !important;
		font-size: 12px !important;
		text-align: center !important;
	}
	
	.animetitle {
		font-weight: normal !important;
		font-style: normal !important;
		color: #414141 !important;
	}
	 

	#mal_control_strip {
		width: 100% !important;
		margin: 0px !important;
		left: 0px !important;
		font-variant: normal !important;
		text-transform: none !important;
		font-size: 11px !important;
	}
	#mal_control_strip td {
	    background: #181818 !important;
		height: 15px !important;
	}
	#mal_control_strip td div {
	    color: #555555 !important;
		margin: 2px 0px !important;
	}
	#mal_cs_listinfo {
		width: 75px !important;
		padding: 0px 1em !important;
	}
	#mal_cs_links {
		width: 150px !important;
		padding: 0px 1em !important;
	}

	#mal_cs_listinfo, #mal_cs_links, #mal_cs_pic {
	    background-color: #181818 !important;
	    border-right: 1px solid !important;
	    border-color: #C4C4C4 !important;
	    color: #555555 !important; 
	}
	#mal_cs_otherlinks {
	    background-color: #181818 !important;
	    color: #aeaeae !important;
	    padding: 0px 1em !important;
	}
	#mal_cs_listinfo a, #mal_cs_links a, #mal_cs_pic a, #mal_cs_otherlinks a {
	    font-size: 12px !important;
		color: #aeaeae !important; 
		font-style: normal !important;
	}

	#copyright, #mal_cs_powered {display: none !important}
}
