@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.ca") {

li.g, body, .std, h1 {
    background-color: #ffffff !important;
    font-family: Segoe UI !important;
}
a {
    color: #e8608c !important;
    font-size: 12px !important;
    font-style: italic !important;
}

a:hover, a:active {
    color: #6bbfe3 !important;
}

a.ab_button, a.ab_button:visited {
    color: #e8608c !important;
    background-color: #ffffff !important;
}

.gb_F {
    background: #e8608c !important;
    border: 0px !important;
    color: #ffffff !important;
    font-style: italic !important;
    font-weight: normal !important;
    
}

.gb_g {
    background-image: url("http://s5.postimg.org/cc6tmcd13/google_ICONS.png") !important;
}

#gbqfb {
    background: #66bfe3 !important;
    box-shadow: none;
    height: 28px !important;
    outline: medium none;
    padding: 0;
    width: 40px !important;
}

.gbqfqw {
    background: none repeat scroll 0 0 padding-box #f7f7f7 !important;
    border-color: #dddddd !important;
    border-image: none !important;
    border-style: solid;
    border-width: 1px 0 1px 1px;
    height: 28px !important;
}

.gbqfba, .gbqfbb {
    border: 1px solid #dddddd !important;
    color: #aeaeae !important;
    font-size: 10px !important;
    height: 27px !important;
}

.gb_bb, .gb_cb {
    background: #f7f7f7 !important;
    border: 1px solid #dddddd !important;
}

#fbar {
    display: none !important;
}

#hdtb_msb .hdtb_mitem.hdtb_msel, #hdtb_msb .hdtb_mitem.hdtb_msel_pre {
    color: #e8608c !important;
    font-weight: normal !important;
    font-style: italic !important;
    font-size: 12px !important;
    border-bottom: 2px solid #6bbfe3 !important;
    
}

.a, cite, cite a:link, cite a:visited, .cite, .cite:link, #ans > i, .bc a:link {
    color: #6bbfe3 !important;
}

